import numpy as np
def softmax(logits: np.ndarray) -> list[list[float]]:
    """Compute a row-wise numerically stable softmax."""
    if logits.ndim == 1:
        logits = logits.reshape(1, -1)
    max_logits = np.max(logits, axis=1, keepdims=True)
    exp_logits = np.exp(logits - max_logits)
    softmax_probs = exp_logits / np.sum(exp_logits, axis=1, keepdims=True)
    return softmax_probs.round(6).tolist()

import numpy as np
def gradient_boosting_regressor(X: list[list[float]], y: list[float], n_estimators: int=10, learning_rate: float=0.1) -> list[float]:
    X = np.array(X, dtype=float)
    y = np.array(y, dtype=float)
    if n_estimators <= 0 or learning_rate == 0:
        return [round(np.mean(y), 4)] * len(y)
    y_hat = np.full_like(y, np.mean(y))
    for t in range(1, n_estimators + 1):
        residuals = y - y_hat
        model = np.linalg.lstsq(X, residuals, rcond=None)[0]
        weak_learner_pred = np.dot(X, model)
        y_hat += learning_rate * weak_learner_pred
    return [round(pred, 4) for pred in y_hat.tolist()]

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """

    def sigmoid_func(val):
        return 1 / (1 + np.exp(-val))

    def sigmoid_derivative(val):
        sig = sigmoid_func(val)
        return sig * (1 - sig)

    def sigmoid_second_derivative(val):
        sig = sigmoid_func(val)
        return sig * (1 - sig) * (1 - 2 * sig)
    if order == 0:
        return np.round(sigmoid_func(x), 4).tolist() if isinstance(x, (list, np.ndarray)) else round(sigmoid_func(x), 4)
    elif order == 1:
        return np.round(sigmoid_derivative(x), 4).tolist() if isinstance(x, (list, np.ndarray)) else round(sigmoid_derivative(x), 4)
    elif order == 2:
        return np.round(sigmoid_second_derivative(x), 4).tolist() if isinstance(x, (list, np.ndarray)) else round(sigmoid_second_derivative(x), 4)
    else:
        return -1

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    if sigma is None:
        sigma = np.sqrt(len(X[0]) / 2)
    elif isinstance(sigma, float) and sigma <= 0:
        return -1
    elif isinstance(sigma, list) and len(sigma) != len(X[0]):
        return -1
    if Y is not None and len(X[0]) != len(Y[0]):
        return -1
    if Y is None:
        Y = X
    kernel_matrix = []
    for x in X:
        row = []
        for y in Y:
            dist_sq = sum(((x[j] - y[j]) / (sigma if isinstance(sigma, float) else sigma[j]) ** 2 for j in range(len(x))))
            row.append(np.exp(-0.5 * dist_sq))
        kernel_matrix.append(row)
    kernel_matrix_rounded = [[round(val, 4) for val in row] for row in kernel_matrix]
    return kernel_matrix_rounded

