def build_kdtree(points, labels, depth=0):
    if not points:
        return None
    axis = depth % len(points[0])
    points.sort(key=lambda x: x[axis])
    median = len(points) // 2
    return Node(point=points[median], label=labels[median], left=build_kdtree(points[:median], labels[:median], depth + 1), right=build_kdtree(points[median + 1:], labels[median + 1:], depth + 1))
def euclidean_distance(p1, p2):
    return math.sqrt(sum(((x - y) ** 2 for (x, y) in zip(p1, p2))))
def knn_search(node, point, k, depth=0, best=None):
    if node is None:
        return best
    axis = depth % len(point)
    next_best = None
    next_branch = None
    if best is None or euclidean_distance(point, node.point) < euclidean_distance(point, best.point):
        next_best = node
    else:
        next_best = best
    if point[axis] < node.point[axis]:
        next_branch = node.left
    else:
        next_branch = node.right
    return knn_search(next_branch, point, k, depth + 1, next_best)
def knn_kdtree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int) -> List[int]:
    if not X_train or k <= 0:
        return []
    kdtree = build_kdtree(X_train, y_train)
    predictions = []
    for test_point in X_test:
        nearest_neighbors = []
        knn_search(kdtree, test_point, k, best=None)
        for neighbor in nearest_neighbors:
            heapq.heappush(nearest_neighbors, (euclidean_distance(test_point, neighbor.point), neighbor.label))
        k_nearest_labels = [label for (_, label) in heapq.nsmallest(k, nearest_neighbors)]
        label_counts = Counter(k_nearest_labels)
        most_common_label = min(label_counts, key=lambda x: (label_counts[x], -x))
        predictions.append(most_common_label)
    return predictions

