import numpy as np
from collections import Counter
def knn_classifier(X_train, y_train, X_query, k, method='naive'):
    """k-Nearest Neighbour classifier.

    Parameters
    ----------
    X_train : list[list[float]] | np.ndarray
        Feature matrix of the training set (n_samples × n_features).
    y_train : list[int] | np.ndarray
        Integer labels for training samples.
    X_query : list[list[float]] | np.ndarray
        Feature matrix of the query/test set.
    k : int
        Number of neighbours to consider.
    method : str, default "naive"
        Neighbour search strategy: "naive", "heap" or "kdtree".

    Returns
    -------
    list[int]
        Predicted label for each query sample.
    """

    def euclidean_distance(x1, x2):
        return np.sqrt(np.sum((x1 - x2) ** 2))

    def get_majority_vote(neighbors):
        vote_counts = Counter(neighbors)
        return vote_counts.most_common(1)[0][0]

    def naive_search(query):
        distances = [euclidean_distance(query, x) for x in X_train]
        nearest_indices = np.argsort(distances)[:k]
        nearest_labels = [y_train[i] for i in nearest_indices]
        return get_majority_vote(nearest_labels)

    def heap_search(query):
        distances = [(euclidean_distance(query, x), i) for (i, x) in enumerate(X_train)]
        heapq.heapify(distances)
        nearest_indices = [i for (_, i) in heapq.nsmallest(k, distances)]
        nearest_labels = [y_train[i] for i in nearest_indices]
        return get_majority_vote(nearest_labels)

    def kdtree_search(query):

        def build_kdtree(points, depth=0):
            if len(points) == 0:
                return None
            axis = depth % len(points[0])
            points.sort(key=lambda x: x[axis])
            median = len(points) // 2
            return {'point': points[median], 'left': build_kdtree(points[:median], depth + 1), 'right': build_kdtree(points[median + 1:], depth + 1)}

        def search_kdtree(tree, query, k, depth=0, best=None):
            if tree is None:
                return best
            axis = depth % len(query)
            next_best = None
            next_branch = None
            if best is None or euclidean_distance(query, best) > euclidean_distance(query, tree['point']):
                next_best = tree['point']
            if query[axis] < tree['point'][axis]:
                next_branch = tree['left']
            else:
                next_branch = tree['right']
            return search_kdtree(next_branch, query, k, depth + 1, next_best)
        kdtree = build_kdtree(X_train)
        nearest_labels = [y_train[X_train.index(search_kdtree(kdtree, query, k))] for _ in range(k)]
        return get_majority_vote(nearest_labels)
    if method == 'naive':
        return [naive_search(query) for query in X_query]
    elif method == 'heap':
        return [heap_search(query) for query in X_query]
    elif method == 'kdtree':
        return [kdtree_search(query) for query in X_query]
    else:
        raise ValueError('Invalid method provided')
