from typing import List, Tuple
def astar(grid: List[List[int]], start: Tuple[int, int], dest: Tuple[int, int]) -> int:
    """A* search on a 2-D grid.

    Args:
        grid: 2-D list containing only 0 (free) and 1 (obstacle).
        start: (row, column) pair indicating the start position.
        dest:  (row, column) pair indicating the goal position.

    Returns:
        The minimum number of moves needed to reach *dest* from *start* using
        4-directional movement, or ‑1 if the destination is unreachable.
    """
    (rows, cols) = (len(grid), len(grid[0]))
    if not (0 <= start[0] < rows and 0 <= start[1] < cols) or not (0 <= dest[0] < rows and 0 <= dest[1] < cols):
        return -1
    if grid[start[0]][start[1]] == 1 or grid[dest[0]][dest[1]] == 1:
        return -1
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    open_set = []
    heapq.heappush(open_set, (0, start))
    g_score = {start: 0}

    def heuristic(a: Tuple[int, int], b: Tuple[int, int]) -> int:
        return abs(a[0] - b[0]) + abs(a[1] - b[1])
    while open_set:
        (current_f, current) = heapq.heappop(open_set)
        if current == dest:
            return g_score[current]
        for direction in directions:
            neighbor = (current[0] + direction[0], current[1] + direction[1])
            if 0 <= neighbor[0] < rows and 0 <= neighbor[1] < cols and (grid[neighbor[0]][neighbor[1]] == 0):
                tentative_g_score = g_score[current] + 1
                if neighbor not in g_score or tentative_g_score < g_score[neighbor]:
                    g_score[neighbor] = tentative_g_score
                    f_score = tentative_g_score + heuristic(neighbor, dest)
                    heapq.heappush(open_set, (f_score, neighbor))
    return -1

import numpy as np
def dbscan(data: np.ndarray, eps: float, min_samples: int) -> list[int]:

    def region_query(data, point_index, eps):
        neighbors = []
        for i in range(len(data)):
            if np.linalg.norm(data[point_index] - data[i]) <= eps:
                neighbors.append(i)
        return neighbors

    def expand_cluster(data, point_index, neighbors, cluster_id, eps, min_samples, clusters, visited):
        clusters[point_index] = cluster_id
        i = 0
        while i < len(neighbors):
            neighbor_index = neighbors[i]
            if visited[neighbor_index] == False:
                visited[neighbor_index] = True
                new_neighbors = region_query(data, neighbor_index, eps)
                if len(new_neighbors) >= min_samples:
                    neighbors += new_neighbors
            if clusters[neighbor_index] == -1:
                clusters[neighbor_index] = cluster_id
            i += 1
    if len(data) == 0:
        return []
    clusters = [-1] * len(data)
    visited = [False] * len(data)
    cluster_id = 0
    for i in range(len(data)):
        if visited[i]:
            continue
        visited[i] = True
        neighbors = region_query(data, i, eps)
        if len(neighbors) < min_samples:
            clusters[i] = -1
        else:
            expand_cluster(data, i, neighbors, cluster_id, eps, min_samples, clusters, visited)
            cluster_id += 1
    return clusters
