def euclidean_distance(a, b):
    if len(a) != len(b):
        return -1
    a = np.array(a).flatten()
    b = np.array(b).flatten()
    distance = np.sqrt(np.sum((a - b) ** 2))
    return round(distance, 4)

import numpy as np
def sigmoid(z):
    """Compute the numerically stable, element-wise sigmoid activation.

    The function must work for a scalar, list/tuple, or NumPy ndarray input.

    Args:
        z: int, float, list, tuple, or numpy.ndarray – input values.

    Returns:
        float if *z* is a scalar, otherwise a Python list obtained with
        ``numpy.ndarray.tolist()``, where every element is the sigmoid of the
        corresponding input value and is rounded to four decimal places.
    """

    def sigmoid_func(x):
        return 1 / (1 + np.exp(-x))
    z = np.array(z)
    sigmoid_z = sigmoid_func(z)
    sigmoid_z_rounded = np.round(sigmoid_z, 4)
    if isinstance(z, (list, tuple)):
        return sigmoid_z_rounded.tolist()
    else:
        return float(sigmoid_z_rounded)

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """
    if isinstance(z, (int, float)):
        return np.round(z * (1 - z), 4)
    elif isinstance(z, list):
        if len(z) == 0:
            return []
        return [sigmoid_grad(val) for val in z]
    elif isinstance(z, np.ndarray):
        return np.round(z * (1 - z), 4)
    else:
        raise ValueError('Input must be a float, list, or NumPy array')

import numpy as np
def relu_grad(z):
    """Compute the element-wise derivative of the ReLU activation.

    The function should return 0 for every entry that is less than or equal to
    zero and 1 for every entry that is strictly greater than zero. Scalars
    must yield a single integer; arrays/lists must yield a list of identical
    shape.
    """
    if isinstance(z, (int, float)):
        return 1 if z > 0 else 0
    elif isinstance(z, np.ndarray):
        grad = (z > 0).astype(int)
        return grad.tolist()
    elif isinstance(z, list):
        grad = [1 if value > 0 else 0 for value in z]
        return grad
    else:
        raise TypeError('Input must be a scalar, list, or NumPy ndarray.')

import numpy as np
def sigmoid_(Z):
    """Compute the logistic sigmoid activation element-wise.

    Args:
        Z: A scalar, 1-D list/array, or 2-D list/array of real numbers.

    Returns:
        Sigmoid-transformed value(s) rounded to 4 decimals.  Returns a float for
        scalar input; otherwise returns a Python list preserving the input
        shape.
    """

    def sigmoid(z):
        return round(1 / (1 + np.exp(-z)), 4)
    if isinstance(Z, (int, float)):
        return sigmoid(Z)
    else:
        Z = np.array(Z)
        sigmoid_values = np.vectorize(sigmoid)(Z)
        if Z.ndim == 0:
            return float(sigmoid_values)
        else:
            return sigmoid_values.tolist()

