import numpy as np
def rmsprop_update(params: list[np.ndarray], grads: list[np.ndarray], s: list[np.ndarray] | None=None, alpha: float=0.01, beta: float=0.9, epsilon: float=1e-08) -> tuple[list[np.ndarray], list[np.ndarray]]:
    if s is None:
        s = [np.zeros_like(param) for param in params]
    new_params = []
    new_s = []
    for (param, grad, s_val) in zip(params, grads, s):
        s_val = beta * s_val + (1 - beta) * np.square(grad)
        updated_param = param - alpha * grad / (np.sqrt(s_val) + epsilon)
        new_params.append(np.round(updated_param, 6))
        new_s.append(s_val)
    return (new_params, new_s)

def tree_stats(root: 'node | None') -> tuple[int, int]:
    """Return the number of leaf nodes and the maximum depth of the tree.

    A **leaf** is defined as a node whose ``results`` attribute is not ``None``.

    Parameters
    ----------
    root : node | None
        Root of the decision tree.  If *None*, the tree is considered empty.

    Returns
    -------
    tuple[int, int]
        ``(leaf_count, max_depth)`` where
        * *leaf_count* is the total number of leaves in the tree, and
        * *max_depth* is the length of the longest path from *root* to a leaf.
    """
    if root is None:
        return (0, 0)

    def traverse(node, depth):
        if node.results is not None:
            return (1, depth)
        else:
            (left_count, left_depth) = traverse(node.left, depth + 1) if node.left else (0, 0)
            (right_count, right_depth) = traverse(node.right, depth + 1) if node.right else (0, 0)
            return (left_count + right_count, max(left_depth, right_depth))
    (leaf_count, max_depth) = traverse(root, 1)
    return (leaf_count, max_depth)

def find_best_split(data: List[List[Any]]) -> Tuple[int, Any]:
    best_gain = 0
    best_feature = None
    best_value = None
    current_impurity = gini_impurity([sample[-1] for sample in data])
    for feature_index in range(len(data[0]) - 1):
        feature_values = set([sample[feature_index] for sample in data])
        for value in feature_values:
            (left_subset, right_subset) = split_data(data, feature_index, value)
            if len(left_subset) == 0 or len(right_subset) == 0:
                continue
            left_impurity = gini_impurity([sample[-1] for sample in left_subset])
            right_impurity = gini_impurity([sample[-1] for sample in right_subset])
            gain = current_impurity - (len(left_subset) / len(data) * left_impurity + len(right_subset) / len(data) * right_impurity)
            if gain > best_gain:
                best_gain = gain
                best_feature = feature_index
                best_value = value
    return (best_feature, best_value)
def build_tree(data: List[List[Any]]) -> Dict[str, Any]:
    if not data:
