import numpy as np
def perceptron_dual_train(X_data: np.ndarray, y_data: np.ndarray, eta: float=1.0, max_iter: int=1000) -> tuple[list[float], float]:
    (N, d) = X_data.shape
    alpha = np.zeros(N)
    b = 0
    G = np.dot(X_data, X_data.T)
    for _ in range(max_iter):
        updated = False
        for i in range(N):
            g = np.sum(alpha * y_data * G[:, i])
            if y_data[i] * (g + b) <= 0:
                alpha[i] += eta
                b += eta * y_data[i]
                updated = True
        if not updated:
            break
    w = np.sum(alpha * y_data[:, np.newaxis] * X_data, axis=0)
    w = np.round(w, 4).tolist()
    b = round(b, 4)
    return (w, b)

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if root.y is not None:
        return root.y
    if task_type == 'classification':
        feature_value = features[root.label]
        for child in root.child:
            if child.x == feature_value:
                return predict_sample(child, features, task_type)
        return predict_sample(root.child[1], features, task_type)
    elif task_type == 'regression':
        feature_value = features[root.label]
        if feature_value <= root.s:
            return predict_sample(root.child[0], features, task_type)
        else:
            return predict_sample(root.child[1], features, task_type)

import numpy as np
import math
def entropy(labels):
    (unique_labels, label_counts) = np.unique(labels, return_counts=True)
    probabilities = label_counts / len(labels)
    return -np.sum(probabilities * np.log2(probabilities))
def conditional_entropy(feature_values, labels):
    (unique_values, value_counts) = np.unique(feature_values, return_counts=True)
    total_entropy = 0
    for (value, count) in zip(unique_values, value_counts):
        subset_labels = labels[feature_values == value]
        total_entropy += count / len(feature_values) * entropy(subset_labels)
    return total_entropy
def choose_best_feature(X: np.ndarray, y: np.ndarray) -> tuple[int, float]:
    num_features = X.shape[1]
    total_entropy = entropy(y)
    max_information_gain = 0
    best_feature_index = 0
    for i in range(num_features):
        feature_entropy = conditional_entropy(X[:, i], y)
        information_gain = total_entropy - feature_entropy
        if information_gain > max_information_gain:
            max_information_gain = information_gain
            best_feature_index = i
    return (best_feature_index, round(max_information_gain, 6))

import numpy as np
import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:

    def c(n):
        if n == 1:
            return 0
        return 2 * np.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    def build_tree(samples, height):
        if height >= height_limit or len(samples) <= 1:
            return {'size': len(samples)}
        feature = np.random.randint(0, data.shape[1])
        (min_val, max_val) = (np.min(samples[:, feature]), np.max(samples[:, feature]))
        threshold = np.random.uniform(min_val, max_val)
        left_samples = samples[samples[:, feature] < threshold]
        right_samples = samples[samples[:, feature] >= threshold]
        return {'feature': feature, 'threshold': threshold, 'left': build_tree(left_samples, height + 1), 'right': build_tree(right_samples, height + 1)}

    def path_length(tree, sample, height):
        if 'size' in tree:
            return height + c(tree['size'])
        if sample[tree['feature']] < tree['threshold']:
            return path_length(tree['left'], sample, height + 1)
        else:
            return path_length(tree['right'], sample, height + 1)
    n = data.shape[0]
    height_limit = math.ceil(np.log2(sample_size))
    forest = [build_tree(data[np.random.choice(n, sample_size, replace=False)], 0) for _ in range(n_trees)]
    path_lengths = np.array([[path_length(tree, sample, 0) for tree in forest] for sample in data])
    mean_path_lengths = np.mean(path_lengths, axis=1)
    phi = 2 * np.log(n - 1) - 2 * (n - 1) / n
    scores = 2 ** (-mean_path_lengths / phi)
    if epsilon == 0:
        return []
    threshold = np.percentile(scores, (1 - epsilon) * 100)
    outliers = np.where(scores > threshold)[0]
    return list(outliers)
