import numpy as np
def local_outlier_factor(data, k, epsilon=1.5):

    def euclidean_distance(x1, x2):
        return np.linalg.norm(x1 - x2)

    def k_distance(data, k):
        distances = np.zeros((len(data), len(data)))
        for i in range(len(data)):
            for j in range(len(data)):
                distances[i, j] = euclidean_distance(data[i], data[j])
        k_distances = np.zeros(len(data))
        for i in range(len(data)):
            sorted_indices = np.argsort(distances[i])
            k_distances[i] = distances[i, sorted_indices[k]]
        return k_distances

    def reachability_distance(data, k):
        k_distances = k_distance(data, k)
        distances = np.zeros((len(data), len(data)))
        for i in range(len(data)):
            for j in range(len(data)):
                distances[i, j] = max(k_distances[j], euclidean_distance(data[i], data[j]))
        return distances

    def local_reachability_density(data, k):
        reach_distances = reachability_distance(data, k)
        lrd = np.zeros(len(data))
        for i in range(len(data)):
            neighbors = np.argsort(reach_distances[i])[:k]
            lrd[i] = k / np.sum(reach_distances[i, neighbors])
        return lrd

    def local_outlier_factor(data, k):
        lrd = local_reachability_density(data, k)
        reach_distances = reachability_distance(data, k)
        lof = np.zeros(len(data))
        for i in range(len(data)):
            neighbors = np.argsort(reach_distances[i])[:k]
            lof[i] = np.sum(lrd[neighbors]) / (k * lrd[i])
        return lof
    lof_scores = local_outlier_factor(data, k)
    rounded_lof_scores = np.round(lof_scores, decimals=4)
    outlier_indices = np.where(rounded_lof_scores > epsilon)[0]
    return (rounded_lof_scores, outlier_indices)

import numpy as np
def nearest_neighbor_graph(X):
    n = len(X)
    if n == 1:
        return [[0]]
    k = min(int(np.floor(np.sqrt(n))), 10)
    distances = np.linalg.norm(X[:, np.newaxis] - X, axis=2)
    np.fill_diagonal(distances, np.inf)
    adjacency_matrix = np.zeros((n, n))
    for i in range(n):
        closest_indices = np.argsort(distances[i])[:k]
        for j in closest_indices:
            if i != j:
                adjacency_matrix[i, j] = 1
    adjacency_matrix = np.maximum(adjacency_matrix, adjacency_matrix.T)
    return adjacency_matrix.astype(int).tolist()

import random
from typing import Any, List, Tuple
def manage_replay_memory(operations: List[Tuple]) -> List[Any]:
    """Perform operations on an in-memory replay buffer.

    The buffer starts out empty and is manipulated according to *operations*.

    Args:
        operations: A list of tuples describing the commands to execute. The
            first element of each tuple is a string ("append", "pop", "sample",
            or "size").  Additional elements contain the arguments of that
            command.

    Returns:
        A list containing the outputs of all commands that return a value, in
        the same order in which those commands appear in *operations*.
    """
    random.seed(0)
    replay_buffer = []
    outputs = []
    for operation in operations:
        if operation[0] == 'append':
            replay_buffer.append(operation[1])
        elif operation[0] == 'pop':
            if replay_buffer:
                outputs.append(replay_buffer.pop())
            else:
                outputs.append(None)
        elif operation[0] == 'sample':
            if len(replay_buffer) >= operation[1]:
                outputs.append(random.sample(replay_buffer, operation[1]))
            else:
                outputs.append([])
        elif operation[0] == 'size':
            outputs.append(len(replay_buffer))
    return outputs

import numpy as np
def minesweeper_step(ground: list[list[float | int]], flags: list[list[bool]]) -> tuple[list[tuple[int, int]], list[tuple[int, int]]]:
    clicks = []
    new_flags = []
    rows = len(ground)
    cols = len(ground[0])
    for r in range(rows):
        for c in range(cols):
            if not np.isnan(ground[r][c]) and (not flags[r][c]):
                value = ground[r][c]
                bombs = 0
                unexplored = 0
                for dr in range(-1, 2):
                    for dc in range(-1, 2):
                        if dr == 0 and dc == 0:
                            continue
                        (nr, nc) = (r + dr, c + dc)
                        if 0 <= nr < rows and 0 <= nc < cols:
                            if flags[nr][nc]:
                                bombs += 1
                            elif np.isnan(ground[nr][nc]):
                                unexplored += 1
                if value == 0:
                    for dr in range(-1, 2):
                        for dc in range(-1, 2):
                            if dr == 0 and dc == 0:
                                continue
                            (nr, nc) = (r + dr, c + dc)
                            if 0 <= nr < rows and 0 <= nc < cols and np.isnan(ground[nr][nc]) and (not flags[nr][nc]):
                                clicks.append((nr, nc))
                elif value == 8:
                    for dr in range(-1, 2):
                        for dc in range(-1, 2):
                            if dr == 0 and dc == 0:
                                continue
                            (nr, nc) = (r + dr, c + dc)
                            if 0 <= nr < rows and 0 <= nc < cols and np.isnan(ground[nr][nc]) and (not flags[nr][nc]):
                                new_flags.append((nr, nc))
                elif bombs == value:
                    for dr in range(-1, 2):
                        for dc in range(-1, 2):
                            if dr == 0 and dc == 0:
                                continue
                            (nr, nc) = (r + dr, c + dc)
                            if 0 <= nr < rows and 0 <= nc < cols and np.isnan(ground[nr][nc]) and (not flags[nr][nc]):
                                clicks.append((nr, nc))
                elif unexplored == value:
                    for dr in range(-1, 2):
                        for dc in range(-1, 2):
                            if dr == 0 and dc == 0:
                                continue
                            (nr, nc) = (r + dr, c + dc)
                            if 0 <= nr < rows and 0 <= nc < cols and np.isnan(ground[nr][nc]) and (not flags[nr][nc]):
                                new_flags.append((nr, nc))
    clicks = sorted(list(set(clicks)))
    new_flags = sorted(list(set(new_flags)))
    return (clicks, new_flags)

from copy import deepcopy
def deduce_mines(constraints: list[list]) -> tuple[list[str], list[str]]:
    """Simplify Minesweeper constraints.

    You receive *constraints* as a list of 2-element lists *[variables, value]*
    where *variables* is a list of identifiers and *value* is an int telling how
    many of those variables hide a mine.

    The function must apply the logical rules described in the task statement
    (solved constraints, propagation, subset rule, duplicate removal) until no
    new information can be obtained.

    The result is a tuple (safe, mines) of two **alphabetically sorted** lists.
    The first element contains all variables that are certainly safe, the second
    one all variables that are certainly mines.  If nothing can be deduced an
    empty list must be returned instead.
    """
    safe = set()
    mines = set()

    def remove_variable(var, constraints):
        for i in range(len(constraints)):
            if var in constraints[i][0]:
                constraints[i][0].remove(var)
                if constraints[i][1] > 0:
                    constraints[i][1] -= 1
    while True:
        new_info = False
        for (variables, value) in constraints:
            if value == 0:
                for var in variables:
                    if var not in safe:
                        safe.add(var)
                        new_info = True
                for var in variables:
                    remove_variable(var, constraints)
            elif value == len(variables):
                for var in variables:
                    if var not in mines:
                        mines.add(var)
                        new_info = True
                for var in variables:
                    remove_variable(var, constraints)
        constraints = [c for c in constraints if c[0]]
        new_constraints = []
        seen = set()
        for i in range(len(constraints)):
            for j in range(len(constraints)):
                if i != j:
                    (S1, v1) = constraints[i]
                    (S2, v2) = constraints[j]
                    if set(S1).issubset(set(S2)):
                        new_value = v2 - v1
                        new_vars = [var for var in S2 if var not in S1]
                        if new_value >= 0 and (tuple(new_vars), new_value) not in seen:
                            new_constraints.append((new_vars, new_value))
                            seen.add((tuple(new_vars), new_value))
        for c in constraints:
            if tuple(c) not in seen:
                new_constraints.append(c)
                seen.add(tuple(c))
        constraints = new_constraints
        seen = set()
        unique_constraints = []
        for c in constraints:
            if tuple(c) not in seen:
                unique_constraints.append(c)
                seen.add(tuple(c))
        constraints = unique_constraints
        if not new_info:
            break
    return (sorted(safe), sorted(mines))
