class Dense(Layer):

    def __init__(self, n_units, input_shape=None):
        self.layer_input = None
        self.input_shape = input_shape
        self.n_units = n_units
        self.trainable = True
        self.W = None
        self.w0 = None
        self.W_optimizer = None
        self.w0_optimizer = None
        if input_shape is not None:
            self.set_input_shape(input_shape)
        self.initialize()

    def initialize(self):
        input_dim = self.input_shape[0] if self.input_shape is not None else None
        if input_dim is None:
            raise ValueError('Input shape must be specified for initialization.')
        limit = 1 / math.sqrt(input_dim)
        self.W = np.random.uniform(-limit, limit, (input_dim, self.n_units))
        self.w0 = np.zeros(self.n_units)
        self.W_optimizer = {'lr': 0.01}
        self.w0_optimizer = {'lr': 0.01}

    def parameters(self):
        return self.W.size + self.w0.size

    def forward_pass(self, X, training=True):
        self.layer_input = X
        output = np.dot(X, self.W) + self.w0
        output = np.round(output, 4)
        return output.tolist()

    def backward_pass(self, accum_grad):
        X = self.layer_input
        grad_W = np.dot(X.T, accum_grad)
        grad_w0 = np.sum(accum_grad, axis=0)
        grad_input = np.dot(accum_grad, self.W.T)
        grad_W = np.round(grad_W, 4)
        grad_w0 = np.round(grad_w0, 4)
        grad_input = np.round(grad_input, 4)
        if self.trainable:
            self.W -= self.W_optimizer['lr'] * grad_W
            self.w0 -= self.w0_optimizer['lr'] * grad_w0
        return grad_input.tolist()

    def output_shape(self):
        return (self.n_units,)
