def compute_tf_idf(corpus, query):
    if not corpus:
        return []
    N = len(corpus)
    vocab = set()
    for doc in corpus:
        vocab.update(doc)
    vocab = list(vocab)
    term_index = {term: idx for (idx, term) in enumerate(vocab)}
    dt_matrix = np.zeros((N, len(vocab)), dtype=float)
    for (doc_idx, doc) in enumerate(corpus):
        for word in doc:
            if word in term_index:
                dt_matrix[doc_idx, term_index[word]] += 1
    df = np.count_nonzero(dt_matrix > 0, axis=0)
    idf = np.log((N + 1) / (df + 1)) + 1
    tf_matrix = np.zeros_like(dt_matrix)
    for (i, doc) in enumerate(corpus):
        total_terms = len(doc)
        if total_terms > 0:
            for word in doc:
                if word in term_index:
                    tf_matrix[i, term_index[word]] += 1
            tf_matrix[i] /= total_terms
    results = []
    for doc_idx in range(N):
        tfidf_scores = []
        for q_word in query:
            if q_word in term_index:
                tf = tf_matrix[doc_idx, term_index[q_word]]
                score = tf * idf[term_index[q_word]]
            else:
                score = 0.0
            tfidf_scores.append(round(score, 4))
        results.append(tfidf_scores)
    return results
