def calculate_brightness(img):
    if not img:
        return -1
    row_length = None
    for row in img:
        if not isinstance(row, list):
            return -1
        if row_length is None:
            row_length = len(row)
            if row_length == 0:
                return -1
        elif len(row) != row_length:
            return -1
    for row in img:
        for pixel in row:
            if not isinstance(pixel, (int, float)):
                return -1
            if pixel < 0 or pixel > 255:
                return -1
    total_pixels = 0
    total_brightness = 0
    for row in img:
        for pixel in row:
            total_brightness += pixel
            total_pixels += 1
    if total_pixels == 0:
        return -1
    average_brightness = total_brightness / total_pixels
    return round(average_brightness, 2)
