import numpy as np
def sigmoid(x):
    """Element-wise logistic sigmoid."""

    def _apply(val):
        return 1 / (1 + np.exp(-val))
    if isinstance(x, (int, float)):
        return round(_apply(x), 4)
    elif isinstance(x, (list, tuple)):
        result = [_apply(v) for v in x]
        return np.array([round(v, 4) for v in result])
    elif isinstance(x, np.ndarray):
        result = _apply(x)
        return np.round(result, 4)
    else:
        raise TypeError('Input must be int, float, list, tuple, or np.ndarray.')
def tanh(x):
    """Element-wise hyperbolic tangent."""

    def _apply(val):
        return np.tanh(val)
    if isinstance(x, (int, float)):
        return round(_apply(x), 4)
    elif isinstance(x, (list, tuple)):
        result = [_apply(v) for v in x]
        return np.array([round(v, 4) for v in result])
    elif isinstance(x, np.ndarray):
        result = _apply(x)
        return np.round(result, 4)
    else:
        raise TypeError('Input must be int, float, list, tuple, or np.ndarray.')
def relu(x):
    """Element-wise Rectified Linear Unit."""

    def _apply(val):
        return max(0, val)
    if isinstance(x, (int, float)):
        return round(_apply(x), 4)
    elif isinstance(x, (list, tuple)):
        result = [_apply(v) for v in x]
        return np.array([round(v, 4) for v in result])
    elif isinstance(x, np.ndarray):
        result = np.maximum(0, x)
        return np.round(result, 4)
    else:
        raise TypeError('Input must be int, float, list, tuple, or np.ndarray.')
def leaky_relu(x, alpha: float=0.01):
    """Element-wise Leaky ReLU with slope alpha for x<0."""

    def _apply(val):
        return val if val >= 0 else alpha * val
    if isinstance(x, (int, float)):
        return round(_apply(x), 4)
    elif isinstance(x, (list, tuple)):
        result = [_apply(v) for v in x]
        return np.array([round(v, 4) for v in result])
    elif isinstance(x, np.ndarray):
        result = np.where(x >= 0, x, alpha * x)
        return np.round(result, 4)
    else:
        raise TypeError('Input must be int, float, list, tuple, or np.ndarray.')
def softmax(x):
    """Numerically stable softmax over a 1-D input."""
    if isinstance(x, (list, tuple)):
        arr = np.array(x, dtype=float)
    elif isinstance(x, np.ndarray):
        arr = x.astype(float)
    elif isinstance(x, (int, float)):
        arr = np.array([x], dtype=float)
    else:
        raise TypeError('Input must be int, float, list, tuple, or np.ndarray.')
    if arr.ndim != 1:
        raise ValueError('Softmax input must be a 1-D array or list.')
    max_val = np.max(arr)
    exps = np.exp(arr - max_val)
    sum_exps = np.sum(exps)
    softmax_vals = exps / sum_exps
    return np.round(softmax_vals, 4)
