def inorder_keys(pairs):
    """Build a BST from (key, value) *pairs* and return keys from an in-order traversal.

    Args:
        pairs (list[tuple[Any, int | float]]): List of (key, numeric value) pairs.

    Returns:
        list: Keys in in-order (ascending by numeric value). Return an empty
        list if *pairs* is empty.
    """

    class Node:

        def __init__(self, key, val):
            self.key = key
            self.val = val
            self.left = None
            self.right = None

        def __lt__(self, other):
            return self.val < other.val

        def __le__(self, other):
            return self.val <= other.val

        def __gt__(self, other):
            return self.val > other.val

        def __ge__(self, other):
            return self.val >= other.val

    def insert(root, node):
        """Insert node into BST rooted at root following the rules."""
        if node < root:
            if root.left is None:
                root.left = node
            else:
                insert(root.left, node)
        elif root.right is None:
            root.right = node
        else:
            insert(root.right, node)

    def inorder_traversal(node):
        """In-order traversal that yields nodes."""
        if node is not None:
            yield from inorder_traversal(node.left)
            yield node
            yield from inorder_traversal(node.right)
    if not pairs:
        return []
    root = Node(pairs[0][0], pairs[0][1])
    for (key, val) in pairs[1:]:
        insert(root, Node(key, val))
    return [node.key for node in inorder_traversal(root)]
