import re
def apply_activation(x, param=None):
    """Apply a chosen activation to the input vector.

    Args:
        x (list[float] | np.ndarray): Input data. Anything that can be
            converted to a NumPy array of floats is accepted.
        param (str | None, optional): Description of the activation. See the
            task specification for the accepted values. If *None* the
            identity function is applied.

    Returns:
        list[float]: Result of applying the activation element-wise, rounded
            to six decimals and converted to a standard Python *list*.
    """
    arr = np.array(x, dtype=float)

    def identity(x):
        return x

    def relu(x):
        return np.maximum(0, x)

    def tanh(x):
        return np.tanh(x)

    def sigmoid(x):
        return 1 / (1 + np.exp(-x))

    def leaky_relu(x, alpha):
        return np.where(x > 0, x, alpha * x)

    def affine(x, slope, intercept):
        return slope * x + intercept
    if param is None or (isinstance(param, str) and param.strip().lower() == 'identity'):
        result = identity(arr)
    elif isinstance(param, str):
        p = param.strip().lower()
        if p == 'relu':
            result = relu(arr)
        elif p == 'tanh':
            result = tanh(arr)
        elif p == 'sigmoid':
            result = sigmoid(arr)
        elif p.startswith('leaky relu'):
            match = re.search('alpha\\s*=\\s*([0-9]*\\.?[0-9]+)', p)
            alpha = 0.01
            if match:
                alpha = float(match.group(1))
            result = leaky_relu(arr, alpha)
        elif p.startswith('affine'):
            slope_match = re.search('slope\\s*=\\s*([0-9]*\\.?[0-9]+)', p)
            intercept_match = re.search('intercept\\s*=\\s*([0-9]*\\.?[0-9]+)', p)
            if slope_match and intercept_match:
                slope = float(slope_match.group(1))
                intercept = float(intercept_match.group(1))
                result = affine(arr, slope, intercept)
            else:
                raise ValueError(f'Unknown activation: {param}')
        else:
            raise ValueError(f'Unknown activation: {param}')
    else:
        raise ValueError(f'Unknown activation: {param}')
    result_rounded = np.round(result, 6)
    return result_rounded.tolist()
