import numpy as np
def glm_links():
    """Construct and return standard GLM link functions.

    Returns
    -------
    dict
        A three-entry dictionary (identity, log, logit) where each entry is a
        dictionary containing callables for the link, its inverse and its
        derivative with respect to μ. All functions must work with numpy
        scalars as well as 1-D/2-D numpy arrays via element-wise operations.
    """
    identity = {'link': lambda mu: mu, 'inv_link': lambda eta: eta, 'deriv': lambda mu: np.ones_like(mu)}
    log_link = {'link': lambda mu: np.log(mu), 'inv_link': lambda eta: np.exp(eta), 'deriv': lambda mu: 1.0 / mu}
    logit_link = {'link': lambda mu: np.log(mu / (1 - mu)), 'inv_link': lambda eta: 1 / (1 + np.exp(-eta)), 'deriv': lambda mu: 1.0 / (mu * (1 - mu))}
    return {'identity': identity, 'log': log_link, 'logit': logit_link}

