from collections import Counter
def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*.

    Your task is to complete this function so that it builds N-gram count
    tables from *corpus* and then returns the total log-probability of
    *sequence* under the resulting unsmoothed language model.

    The return value must be rounded to 4 decimal places.  If any required
    count is zero you should immediately return ``float('-inf')``.
    """
    if len(sequence) < N:
        return float('-inf')
    ngram_counts = Counter()
    prefix_counts = Counter()
    for i in range(len(corpus) - N + 1):
        ngram = tuple(corpus[i:i + N])
        ngram_counts[ngram] += 1
    if N > 1:
        for i in range(len(corpus) - N + 1):
            prefix = tuple(corpus[i:i + N - 1])
            prefix_counts[prefix] += 1
    else:
        total_tokens = len(corpus)
        prefix_counts[()] = total_tokens
    total_log_prob = 0.0
    for i in range(len(sequence) - N + 1):
        ngram = tuple(sequence[i:i + N])
        if N == 1:
            count_ngram = ngram_counts.get(ngram, 0)
            total_tokens = len(corpus)
            if count_ngram == 0:
                return float('-inf')
            prob = count_ngram / total_tokens
        else:
            count_ngram = ngram_counts.get(ngram, 0)
            prefix = ngram[:-1]
            count_prefix = prefix_counts.get(prefix, 0)
            if count_ngram == 0 or count_prefix == 0:
                return float('-inf')
            prob = count_ngram / count_prefix
        total_log_prob += np.log(prob)
    return round(total_log_prob, 4)

import numpy as np
def forward_algorithm(S: list[float], A: list[list[float]], B: list[list[float]], observations: list[int]) -> float:
    """Forward algorithm for Hidden Markov Models.

    Args:
        S (list[float]): Initial state probabilities.
        A (list[list[float]]): State–transition probabilities.
        B (list[list[float]]): Emission probabilities.
        observations (list[int]): Observation index sequence.

    Returns:
        float: Sequence likelihood rounded to 4 decimals, or −1 on invalid input.
    """
    try:
        S = np.array(S, dtype=float)
        A = np.array(A, dtype=float)
        B = np.array(B, dtype=float)
    except Exception:
        return -1
    n = len(S)
    if n == 0:
        return -1
    if A.shape != (n, n):
        return -1
    if B.shape[0] != n:
        return -1
    m = B.shape[1]
    if not observations:
        return -1
    for obs in observations:
        if not isinstance(obs, int) or obs < 0 or obs >= m:
            return -1
    T = len(observations)
    alpha = np.zeros((T, n))
    alpha[0, :] = S * B[:, observations[0]]
    for t in range(1, T):
        for j in range(n):
            alpha[t, j] = np.sum(alpha[t - 1, :] * A[:, j]) * B[j, observations[t]]
    prob = np.sum(alpha[T - 1, :])
    return round(prob, 4)

from collections import Counter, defaultdict
def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth.

    A *transaction* is represented by a list of items (strings).  `min_support`
    is the minimum number of transactions an item-set has to appear in.  The
    function returns **all** frequent item-sets where
        support(itemset) >= min_support.

    The result must be deterministic:
      • Inside each item-set the items have to be sorted alphabetically.
      • The outer list has to be sorted by `(len(itemset), itemset)`.
    If *transactions* is empty or no item-set meets the threshold return an
    empty list.
    """
    item_support = Counter()
    for transaction in transactions:
        for item in transaction:
            item_support[item] += 1
    frequent_items = {item for (item, count) in item_support.items() if count >= min_support}
    if not frequent_items:
        return []

    class FPNode:

        def __init__(self, item, count, parent):
            self.item = item
            self.count = count
            self.parent = parent
            self.children = {}
            self.link = None

        def increment(self, count):
            self.count += count
    header_table = {}
    for item in frequent_items:
        header_table[item] = None

    def insert_transaction(tree_root, transaction, count=1):
        current_node = tree_root
        for item in transaction:
            if item in current_node.children:
                current_node.children[item].increment(count)
            else:
                new_node = FPNode(item, count, current_node)
                current_node.children[item] = new_node
                if header_table[item] is None:
                    header_table[item] = new_node
                else:
                    node = header_table[item]
                    while node.link is not None:
                        node = node.link
                    node.link = new_node
            current_node = current_node.children[item]
    tree_root = FPNode(None, 0, None)
    for transaction in transactions:
        filtered = [item for item in transaction if item in frequent_items]
        sorted_items = sorted(filtered)
        insert_transaction(tree_root, sorted_items)

    def mine_fp_tree(tree_root, header_table, prefix):
        items = sorted(header_table.items(), key=lambda x: x[1].count if x[1] else 0)
        frequent_itemsets = []
        for (item, node) in items:
            new_prefix = prefix + [item]
            new_prefix_sorted = sorted(new_prefix)
            frequent_itemsets.append(new_prefix_sorted)
            conditional_patterns = []
            node_link = node
            while node_link is not None:
                path = []
                parent = node_link.parent
                while parent is not None and parent.item is not None:
                    path.append(parent.item)
                    parent = parent.parent
                path.reverse()
                if path:
                    for _ in range(node_link.count):
                        conditional_patterns.append(path)
                node_link = node_link.link
            cond_support = Counter()
            for pattern in conditional_patterns:
                for item_in_pattern in pattern:
                    cond_support[item_in_pattern] += 1
            cond_frequent_items = {item for (item, count) in cond_support.items() if count >= min_support}
            if not cond_frequent_items:
                continue

            def build_conditional_tree():
                cond_tree_root = FPNode(None, 0, None)
                cond_header_table = {}
                for item in cond_frequent_items:
                    cond_header_table[item] = None
                for pattern in conditional_patterns:
                    filtered_pattern = [i for i in pattern if i in cond_frequent_items]
                    sorted_pattern = sorted(filtered_pattern)
                    insert_transaction(cond_tree_root, sorted_pattern)
                return (cond_tree_root, cond_header_table)
            (cond_tree_root, cond_header_table) = build_conditional_tree()
            if any(cond_header_table.values()):
                frequent_itemsets.extend(mine_fp_tree(cond_tree_root, cond_header_table, new_prefix))
        return frequent_itemsets
    result = mine_fp_tree(tree_root, header_table, [])
    unique_result = []
    seen = set()
    for itemset in result:
        t = tuple(itemset)
        if t not in seen:
            seen.add(t)
            unique_result.append(itemset)
    unique_result.sort(key=lambda x: (len(x), x))
    return unique_result

from collections import deque
import math
def escape_fire_maze(grid: list[str]) -> int:
    """Escape from a maze with spreading fire.

    Parameters
    ----------
    grid : list[str]
        Square maze represented as a list of strings. Each character must be
        '.', '#', or 'F'.  The agent starts at the upper-left corner (0,0) and
        wishes to reach the lower-right corner (n-1,n-1).  Fire starts in every
        cell marked with 'F' and spreads to the four neighbouring cells every
        minute.  The fire spreads first, then the agent moves.

    Returns
    -------
    int
        Minimum number of minutes required for the agent to reach the goal
        without entering a burning cell, or -1 if this is impossible.
    """
    n = len(grid)
    if n == 0:
        return -1
    if n == 1:
        if grid[0][0] == '.':
            return 0
        else:
            return -1
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    fire_time = [[math.inf for _ in range(n)] for _ in range(n)]
    fire_queue = deque()
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                fire_time[i][j] = 0
                fire_queue.append((i, j))
    while fire_queue:
        (x, y) = fire_queue.popleft()
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n:
                if grid[nx][ny] != '#' and fire_time[nx][ny] == math.inf:
                    fire_time[nx][ny] = fire_time[x][y] + 1
                    fire_queue.append((nx, ny))
    if fire_time[0][0] == 0 or fire_time[n - 1][n - 1] == 0:
        return -1
    agent_queue = deque()
    visited = [[math.inf for _ in range(n)] for _ in range(n)]
    agent_queue.append((0, 0, 0))
    visited[0][0] = 0
    while agent_queue:
        (x, y, time) = agent_queue.popleft()
        if x == n - 1 and y == n - 1:
            return time
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            nt = time + 1
            if 0 <= nx < n and 0 <= ny < n:
                if grid[nx][ny] != '#' and visited[nx][ny] > nt:
                    if fire_time[nx][ny] > nt:
                        visited[nx][ny] = nt
                        agent_queue.append((nx, ny, nt))
    return -1

import numpy as np
def knn_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int, metric: str='euclidean') -> np.ndarray:
    """Implement your code here."""

    def compute_distance(a, b, metric_type):
        if metric_type == 'euclidean':
            return np.linalg.norm(a - b)
        elif metric_type == 'manhattan':
            return np.sum(np.abs(a - b))
        elif metric_type == 'cosine':
            epsilon = 1e-10
            a_norm = np.linalg.norm(a) + epsilon
            b_norm = np.linalg.norm(b) + epsilon
            cosine_similarity = np.dot(a, b) / (a_norm * b_norm)
            return 1 - cosine_similarity
        else:
            return np.linalg.norm(a - b)
    predictions = np.empty(X_test.shape[0], dtype=y_train.dtype)
    for (i, test_sample) in enumerate(X_test):
        distances = np.array([compute_distance(test_sample, train_sample, metric) for train_sample in X_train])
        neighbor_indices = np.argpartition(distances, k)[:k]
        neighbor_labels = y_train[neighbor_indices]
        (labels, counts) = np.unique(neighbor_labels, return_counts=True)
        max_count = np.max(counts)
        candidate_labels = labels[counts == max_count]
        predicted_label = np.min(candidate_labels)
        predictions[i] = predicted_label
    return predictions

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """
    name = name.lower()

    def validate_vectors(x, y):
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return False
        try:
            x_arr = np.array(x, dtype=float)
            y_arr = np.array(y, dtype=float)
        except (ValueError, TypeError):
            return False
        if x_arr.ndim != 1 or y_arr.ndim != 1:
            return False
        if x_arr.size == 0 or y_arr.size == 0:
            return False
        if x_arr.shape != y_arr.shape:
            return False
        return True

    def euclidean(x, y):
        if not validate_vectors(x, y):
            return -1
        x_arr = np.array(x, dtype=float)
        y_arr = np.array(y, dtype=float)
        dist = np.sqrt(np.sum((x_arr - y_arr) ** 2))
        return round(dist, 4)

    def manhattan(x, y):
        if not validate_vectors(x, y):
            return -1
        x_arr = np.array(x, dtype=float)
        y_arr = np.array(y, dtype=float)
        dist = np.sum(np.abs(x_arr - y_arr))
        return round(dist, 4)

    def chebyshev(x, y):
        if not validate_vectors(x, y):
            return -1
        x_arr = np.array(x, dtype=float)
        y_arr = np.array(y, dtype=float)
        dist = np.max(np.abs(x_arr - y_arr))
        return round(dist, 4)

    def cosine(x, y):
        if not validate_vectors(x, y):
            return -1
        x_arr = np.array(x, dtype=float)
        y_arr = np.array(y, dtype=float)
        norm_x = np.linalg.norm(x_arr)
        norm_y = np.linalg.norm(y_arr)
        if norm_x == 0 or norm_y == 0:
            return -1
        dot_product = np.dot(x_arr, y_arr)
        cosine_similarity = dot_product / (norm_x * norm_y)
        dist = 1 - cosine_similarity
        return round(dist, 4)
    metrics_map = {'euclidean': euclidean, 'manhattan': manhattan, 'chebyshev': chebyshev, 'cosine': cosine}

    def distance_func(x, y):
        if name not in metrics_map:
            return -1
        return metrics_map[name](x, y)
    return distance_func

import numpy as np
from itertools import product
from typing import Any, List, Tuple, Union
def action_stats(env: Any, md_action: bool, cont_action: bool) -> Tuple[List[Union[int, float]], Union[List[Tuple[int, ...]], None], int]:
    """Summarise an RL environment's action space.

    Args:
        env: Environment instance exposing an ``action_space`` attribute mimicking OpenAI Gym.
        md_action: ``True`` if the action space is multi-dimensional.
        cont_action: ``True`` if the action space is continuous (infinite number of actions).

    Returns:
        A tuple ``(n_actions_per_dim, action_ids, action_dim)`` where
            • n_actions_per_dim: list with the number of actions in every dimension
                                  (``numpy.inf`` for continuous ones),
            • action_ids: list of all discrete actions (cartesian product) or ``None`` if
                           any dimension is continuous,
            • action_dim: number of action dimensions.
    """
    n_actions_per_dim: List[Union[int, float]] = []
    action_ids: Union[List[Tuple[int, ...]], None] = None
    action_dim: int = 0

    def process_subspace(space):
        if hasattr(space, 'n'):
            return (space.n, False)
        elif hasattr(space, 'shape'):
            return (np.inf, True)
        else:
            raise ValueError('Unknown sub-space type.')
    if hasattr(env.action_space, 'spaces'):
        sub_spaces = env.action_space.spaces
        action_dim = len(sub_spaces)
        for sub_space in sub_spaces:
            (n_actions, is_continuous) = process_subspace(sub_space)
            n_actions_per_dim.append(n_actions)
            if is_continuous:
                action_ids = None
        if action_ids is None:
            pass
        else:
            ranges = []
            for n in n_actions_per_dim:
                ranges.append(range(n))
            action_ids = list(product(*ranges))
    else:
        (n_actions, is_continuous) = process_subspace(env.action_space)
        if md_action:
            action_dim = 1
            n_actions_per_dim = [n_actions]
            if is_continuous:
                action_ids = None
            else:
                ranges = [range(n_actions)]
                action_ids = list(product(*ranges))
        else:
            action_dim = 1
            n_actions_per_dim = [n_actions]
            if is_continuous:
                action_ids = None
            else:
                action_ids = list(product(range(n_actions)))
    if hasattr(env.action_space, 'shape') and md_action:
        shape = env.action_space.shape
        action_dim = len(shape)
        n_actions_per_dim = []
        for dim_size in shape:
            if cont_action:
                n_actions_per_dim.append(np.inf)
            else:
                n_actions_per_dim.append(int(dim_size))
        if cont_action:
            action_ids = None
        else:
            ranges = [range(int(size)) for size in shape]
            action_ids = list(product(*ranges))
    elif hasattr(env.action_space, 'shape') and (not md_action):
        shape = env.action_space.shape
        action_dim = len(shape)
        if cont_action:
            n_actions_per_dim = [np.inf] * len(shape)
            action_ids = None
        else:
            n_actions_per_dim = [int(s) for s in shape]
            ranges = [range(int(s)) for s in shape]
            action_ids = list(product(*ranges))
    if any((isinstance(n, float) and np.isinf(n) for n in n_actions_per_dim)):
        action_ids = None
    return (n_actions_per_dim, action_ids, action_dim)

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack."""

    def hand_value(hand: list[int]) -> int:
        total = sum(hand)
        if 1 in hand:
            if total + 10 <= 21:
                return total + 10
        return total

    def is_blackjack(hand: list[int]) -> bool:
        return len(hand) == 2 and 1 in hand and any((card == 10 for card in hand))
    player_score = hand_value(player)
    dealer_score = hand_value(dealer)
    player_bust = player_score > 21
    dealer_bust = dealer_score > 21
    player_blackjack = is_blackjack(player)
    dealer_blackjack = is_blackjack(dealer)
    if player_bust:
        return -1
    if dealer_bust:
        return 1
    if player_score > dealer_score:
        if player_blackjack:
            return 1.5
        else:
            return 1
    elif player_score < dealer_score:
        return -1
    else:
        return 0

import numpy as np
def average_pool2d(X: np.ndarray, pool_shape: tuple[int, int], stride: tuple[int, int], accum_grad: np.ndarray) -> tuple[list, list]:
    """Performs forward and backward passes of a 2-D average-pooling layer.

    The function must return a tuple (pooled_output, grad_input), both rounded
    to the nearest 4-th decimal and converted to regular Python lists using
    `tolist()`.
    """
    (N, C, H, W) = X.shape
    (p_h, p_w) = pool_shape
    (s_h, s_w) = stride
    out_h = (H - p_h) // s_h + 1
    out_w = (W - p_w) // s_w + 1
    pooled = np.zeros((N, C, out_h, out_w))
    grad_input = np.zeros_like(X)
    for n in range(N):
        for c in range(C):
            for i in range(out_h):
                for j in range(out_w):
                    h_start = i * s_h
                    w_start = j * s_w
                    window = X[n, c, h_start:h_start + p_h, w_start:w_start + p_w]
                    pooled[n, c, i, j] = np.mean(window)
    for n in range(N):
        for c in range(C):
            for i in range(out_h):
                for j in range(out_w):
                    h_start = i * s_h
                    w_start = j * s_w
                    window_grad = accum_grad[n, c, i, j] / (p_h * p_w)
                    grad_input[n, c, h_start:h_start + p_h, w_start:w_start + p_w] += window_grad
    pooled_rounded = np.round(pooled, 4)
    grad_input_rounded = np.round(grad_input, 4)
    return (pooled_rounded.tolist(), grad_input_rounded.tolist())

import numpy as np
def conv2D(X: 'np.ndarray', W: 'np.ndarray', stride: int, pad, dilation: int=0):
    """Performs a 2-D convolution (cross-correlation).

    Args:
        X: NumPy array of shape ``(n_ex, in_rows, in_cols, in_ch)`` representing the input batch.
        W: NumPy array of shape ``(kernel_rows, kernel_cols, in_ch, out_ch)`` containing the kernels.
        stride: Stride of the convolution.
        pad: Padding specification – integer, tuple or the string ``'same'``.
        dilation: Optional dilation factor. ``0`` corresponds to a normal convolution.

    Returns:
        The convolution result **as a Python list** obtained through ``numpy.ndarray.tolist()``.
    """
    (n_ex, in_rows, in_cols, in_ch) = X.shape
    (k_rows, k_cols, in_ch_W, out_ch) = W.shape
    assert in_ch == in_ch_W, 'Input channels of X and W must match.'
    d = dilation + 1
    k_rows_eff = k_rows + (k_rows - 1) * (d - 1)
    k_cols_eff = k_cols + (k_cols - 1) * (d - 1)

    def get_padding(pad, in_size, kernel_size, stride, dilation):
        if isinstance(pad, str) and pad.lower() == 'same':
            out_size = int(np.ceil(float(in_size) / float(stride)))
            pad_total = max((out_size - 1) * stride + kernel_size - in_size, 0)
            pad_before = pad_total // 2
            pad_after = pad_total - pad_before
            return (pad_before, pad_after)
        elif isinstance(pad, int):
            return (pad, pad)
        elif isinstance(pad, tuple) and len(pad) == 2:
            return (pad[0], pad[1])
        elif isinstance(pad, tuple) and len(pad) == 4:
            (pr1, pr2, pc1, pc2) = pad
            return (pr1, pr2, pc1, pc2)
        else:
            return (0, 0)
    if isinstance(pad, str) and pad.lower() == 'same':
        (pad_top, pad_bottom) = get_padding(pad, in_rows, k_rows, stride, d)
        (pad_left, pad_right) = get_padding(pad, in_cols, k_cols, stride, d)
    elif isinstance(pad, int):
        pad_top = pad_bottom = pad_left = pad_right = pad
    elif isinstance(pad, tuple):
        if len(pad) == 2:
            (pad_top, pad_bottom) = get_padding(pad[0], in_rows, k_rows, stride, d)
            (pad_left, pad_right) = get_padding(pad[1], in_cols, k_cols, stride, d)
        elif len(pad) == 4:
            (pr1, pr2, pc1, pc2) = pad
            (pad_top, pad_bottom) = (pr1, pr2)
            (pad_left, pad_right) = (pc1, pc2)
        else:
            pad_top = pad_bottom = pad_left = pad_right = 0
    else:
        pad_top = pad_bottom = pad_left = pad_right = 0
    X_padded = np.pad(X, ((0, 0), (pad_top, pad_bottom), (pad_left, pad_right), (0, 0)), mode='constant', constant_values=0)
    out_rows = (in_rows + pad_top + pad_bottom - d * (k_rows - 1) - 1) // stride + 1
    out_cols = (in_cols + pad_left + pad_right - d * (k_cols - 1) - 1) // stride + 1
    patches = []
    for i in range(out_rows):
        for j in range(out_cols):
            row_start = i * stride
            col_start = j * stride
            patch = X_padded[:, row_start:row_start + d * (k_rows - 1) + 1:d, col_start:col_start + d * (k_cols - 1) + 1:d, :]
            patch_reshaped = patch.reshape(n_ex, -1)
            patches.append(patch_reshaped)
    im2col = np.vstack(patches)
    W_reshaped = W.reshape(-1, out_ch)
    conv_out = im2col @ W_reshaped
    conv_out = conv_out.reshape(n_ex, out_rows, out_cols, out_ch)
    return conv_out.tolist()

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def check_space(space: Space, is_tuple: bool) -> bool:
        if is_tuple:
            if isinstance(space, Tuple):
                return all((check_space(sub_space, False) for sub_space in space.spaces))
            elif isinstance(space, Dict):
                return all((check_space(sub_space, False) for sub_space in space.spaces.values()))
            else:
                return False
        else:
            return isinstance(space, Box)
    cont_action = check_space(env.action_space, tuple_action)
    cont_obs = check_space(env.observation_space, tuple_obs)
    return (cont_action, cont_obs)

import numpy as np
def bayesian_linear_regression_pred(X: np.ndarray, y: np.ndarray, X_new: np.ndarray, mu: float | np.ndarray=0.0, sigma: float=1.0, V: float | np.ndarray | None=None, fit_intercept: bool=True) -> list[float]:
    """Bayesian linear regression with known variance.

    The function returns the MAP predictive means for every row in `X_new`.
    """
    if fit_intercept:
        intercept_column = np.ones((X.shape[0], 1))
        X = np.hstack((intercept_column, X))
        X_new = np.hstack((np.ones((X_new.shape[0], 1)), X_new))
    (N, M) = X.shape
    if np.isscalar(mu):
        mu_vec = np.full(M, mu)
    else:
        mu_vec = np.array(mu)
        if mu_vec.shape[0] != M:
            raise ValueError(f'mu vector length {mu_vec.shape[0]} does not match number of features {M}')
    if V is None:
        V_mat = np.eye(M)
    elif np.isscalar(V):
        V_mat = V * np.eye(M)
    else:
        V_array = np.array(V)
        if V_array.ndim == 1:
            V_mat = np.diag(V_array)
        elif V_array.ndim == 2:
            V_mat = V_array
            if not np.allclose(V_mat, V_mat.T):
                raise ValueError('V matrix must be symmetric.')
        else:
            raise ValueError('V must be None, scalar, 1D array, or 2D array.')
    V_inv = np.linalg.inv(V_mat)
    Sigma_p = np.linalg.inv(V_inv + X.T @ X)
    mu_p = Sigma_p @ (V_inv @ mu_vec + X.T @ y)
    y_pred = X_new @ mu_p
    return [round(val, 4) for val in y_pred]

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in [0, 1, 2]:
        return -1
    arr = np.array(x, dtype=float)
    sigma = 1 / (1 + np.exp(-arr))
    if order == 1:
        result = sigma * (1 - sigma)
    elif order == 2:
        result = sigma * (1 - sigma) * (1 - 2 * sigma)
    else:
        result = sigma
    result = np.round(result, 4)
    if np.isscalar(x):
        return float(result)
    else:
        return result.tolist()

import itertools
from collections import defaultdict
from typing import Hashable
def apriori_frequent_itemsets(transactions: list[list[Hashable]], min_sup: float) -> list[tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """
    total_transactions = len(transactions)
    if total_transactions == 0:
        return []
    item_counts = defaultdict(int)
    for transaction in transactions:
        for item in transaction:
            item_counts[item] += 1
    frequent_itemsets = []
    current_frequent = []
    for (item, count) in item_counts.items():
        support = count / total_transactions
        if support >= min_sup:
            current_frequent.append((item,))
    current_frequent = sorted(current_frequent, key=lambda x: x[0])
    frequent_itemsets.extend(current_frequent)
    k = 2
    prev_frequent = current_frequent
    while prev_frequent:
        candidates = set()
        prev_frequent_sorted = [list(itemset) for itemset in prev_frequent]
        len_prev = len(prev_frequent_sorted)
        for i in range(len_prev):
            for j in range(i + 1, len_prev):
                if prev_frequent_sorted[i][:-1] == prev_frequent_sorted[j][:-1]:
                    candidate = tuple(sorted(set(prev_frequent_sorted[i]) | set(prev_frequent_sorted[j])))
                    if len(candidate) == k:
                        candidates.add(candidate)
        pruned_candidates = set()
        for candidate in candidates:
            subsets = itertools.combinations(candidate, k - 1)
            if all((tuple(sorted(subset)) in prev_frequent for subset in subsets)):
                pruned_candidates.add(candidate)
        candidate_counts = defaultdict(int)
        for transaction in transactions:
            transaction_set = set(transaction)
            for candidate in pruned_candidates:
                if set(candidate).issubset(transaction_set):
                    candidate_counts[candidate] += 1
        current_frequent = []
        for (candidate, count) in candidate_counts.items():
            support = count / total_transactions
            if support >= min_sup:
                current_frequent.append(candidate)
        current_frequent = sorted(current_frequent, key=lambda x: x)
        if current_frequent:
            frequent_itemsets.extend(current_frequent)
        else:
            break
        prev_frequent = current_frequent
        k += 1

    def sort_key(itemset):
        return (len(itemset), itemset)
    return sorted(frequent_itemsets, key=sort_key)

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    try:
        X_np = np.array(X, dtype=float)
    except Exception:
        return -1
    if X_np.ndim != 2:
        return -1
    (N, C) = X_np.shape
    if Y is None:
        Y_np = X_np
    else:
        try:
            Y_np = np.array(Y, dtype=float)
        except Exception:
            return -1
        if Y_np.ndim != 2:
            return -1
        if Y_np.shape[1] != C:
            return -1
    if sigma is None:
        sigma_value = np.sqrt(C / 2)
        sigma_array = np.full(C, sigma_value)
    elif isinstance(sigma, (float, int)):
        if sigma <= 0:
            return -1
        sigma_array = np.full(C, float(sigma))
    elif isinstance(sigma, list) or isinstance(sigma, np.ndarray):
        sigma_list = np.array(sigma, dtype=float)
        if sigma_list.shape != (C,):
            return -1
        if np.any(sigma_list <= 0):
            return -1
        sigma_array = sigma_list
    else:
        return -1
    X_scaled = X_np / sigma_array
    Y_scaled = Y_np / sigma_array
    X_sq = np.sum(X_scaled ** 2, axis=1).reshape(-1, 1)
    Y_sq = np.sum(Y_scaled ** 2, axis=1).reshape(1, -1)
    cross_term = np.dot(X_scaled, Y_scaled.T)
    dist_sq = X_sq + Y_sq - 2 * cross_term
    dist_sq = np.maximum(dist_sq, 0)
    kernel_matrix = np.exp(-0.5 * dist_sq)
    kernel_matrix_rounded = np.round(kernel_matrix, 4)
    result = kernel_matrix_rounded.tolist()
    return result

from collections import Counter, defaultdict
import itertools
from typing import List, Tuple, Hashable, Iterable
def fp_growth(transactions: Iterable[Iterable[Hashable]], min_sup: int) -> List[Tuple]:
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """
    item_counts = Counter()
    for transaction in transactions:
        for item in transaction:
            item_counts[item] += 1
    frequent_items = {item for (item, count) in item_counts.items() if count >= min_sup}
    if not frequent_items:
        return []
    header_table = {}
    for item in frequent_items:
        header_table[item] = [item_counts[item], []]

    class FPNode:

        def __init__(self, item, count, parent):
            self.item = item
            self.count = count
            self.parent = parent
            self.children = {}

        def increment(self, count):
            self.count += count

        def path(self):
            node = self
            path_items = []
            while node.parent is not None:
                node = node.parent
                if node.item is not None:
                    path_items.append(node.item)
            return path_items[::-1]
    root = FPNode(None, 0, None)

    def insert_transaction(transaction, node):
        for item in transaction:
            if item in node.children:
                node.children[item].increment(1)
            else:
                new_node = FPNode(item, 1, node)
                node.children[item] = new_node
                header_table[item][1].append(new_node)
            node = node.children[item]
    for transaction in transactions:
        filtered = [item for item in transaction if item in frequent_items]
        ordered = sorted(filtered, key=lambda x: item_counts[x], reverse=True)
        insert_transaction(ordered, root)

    def mine_fp_tree(node, suffix):
        items = sorted(header_table.items(), key=lambda x: x[1][0])
        frequent_itemsets = []
        for (item, (count, node_links)) in items:
            new_suffix = (item,) + suffix
            frequent_itemsets.append(tuple(sorted(new_suffix)))
            conditional_pattern_base = []
            for n in node_links:
                path = n.path()
                if path:
                    conditional_pattern_base.extend([path] * n.count)
            cond_item_counts = Counter()
            for trans in conditional_pattern_base:
                for itm in trans:
                    cond_item_counts[itm] += 1
            cond_frequent_items = {itm for (itm, cnt) in cond_item_counts.items() if cnt >= min_sup}
            if not cond_frequent_items:
                continue
            cond_header_table = {}
            for itm in cond_frequent_items:
                cond_header_table[itm] = [cond_item_counts[itm], []]
            cond_root = FPNode(None, 0, None)

            def insert_conditional_transaction(transaction, node):
                for itm in transaction:
                    if itm in cond_frequent_items:
                        if itm in node.children:
                            node.children[itm].increment(1)
                        else:
                            new_node = FPNode(itm, 1, node)
                            node.children[itm] = new_node
                            cond_header_table[itm][1].append(new_node)
                        node = node.children[itm]
            for trans in conditional_pattern_base:
                filtered_trans = [itm for itm in trans if itm in cond_frequent_items]
                ordered_trans = sorted(filtered_trans, key=lambda x: cond_item_counts[x], reverse=True)
                insert_conditional_transaction(ordered_trans, cond_root)
            frequent_itemsets.extend(mine_fp_tree(cond_root, new_suffix))
        return frequent_itemsets
    result = mine_fp_tree(root, ())
    unique_result = set(result)
    sorted_result = sorted(unique_result, key=lambda x: (len(x), x))
    return sorted_result

import numpy as np
from typing import Callable
def unhot(function: Callable) -> Callable:
    """Decorator that converts one-hot encoded label arrays to 1-D class labels.

    If either *actual* or *predicted* is a 2-D array whose second dimension is
    larger than one, the array is assumed to be one-hot encoded and is replaced
    by its ``argmax`` along axis 1 before the wrapped *function* is executed.

    Args:
        function: A metric function accepting two NumPy arrays ``(actual,
            predicted)`` and returning a value of any type.

    Returns:
        Callable: A new function that performs the described conversion and then
        calls *function*.
    """

    def wrapper(actual: np.ndarray, predicted: np.ndarray):
        if actual.ndim == 2 and actual.shape[1] > 1:
            actual = actual.argmax(axis=1)
        elif actual.ndim == 2 and actual.shape[1] == 1:
            actual = actual.ravel()
        if predicted.ndim == 2 and predicted.shape[1] > 1:
            predicted = predicted.argmax(axis=1)
        elif predicted.ndim == 2 and predicted.shape[1] == 1:
            predicted = predicted.ravel()
        return function(actual, predicted)
    return wrapper

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """
    arr = np.array(z, dtype=float)
    grad = arr * (1 - arr)
    grad_rounded = np.round(grad, 4)
    if np.isscalar(z):
        return float(grad_rounded)
    elif isinstance(z, list):
        return grad_rounded.tolist()
    else:
        return grad_rounded

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    current_node = root
    while True:
        if hasattr(current_node, 'y'):
            return current_node.y
        feature_index = current_node.label
        feature_value = features[feature_index]
        if task_type == 'classification':
            matched_child = None
            for child in current_node.child:
                if hasattr(child, 'x') and child.x == feature_value:
                    matched_child = child
                    break
            if matched_child is None:
                matched_child = current_node.child[1]
            current_node = matched_child
        elif task_type == 'regression':
            if feature_value <= current_node.s:
                current_node = current_node.child[0]
            else:
                current_node = current_node.child[1]
        else:
            raise ValueError("Invalid task_type. Must be 'classification' or 'regression'.")

import numpy as np
import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)
    (n_samples, n_features) = data.shape
    if epsilon == 0:
        return []
    c_values = [0.0]
    for n in range(2, sample_size + 1):
        c_n = 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n
        c_values.append(c_n)

    def c(n):
        if n <= 1:
            return 0.0
        return c_values[n - 1]

    def build_tree(subsample_indices, depth, height_limit):
        if depth >= height_limit or len(subsample_indices) <= 1:
            return {'size': len(subsample_indices), 'indices': subsample_indices, 'split_feature': None, 'split_value': None, 'left': None, 'right': None}
        current_data = data[subsample_indices]
        feature_idx = np.random.randint(n_features)
        feature_values = current_data[:, feature_idx]
        min_val = feature_values.min()
        max_val = feature_values.max()
        if min_val == max_val:
            return {'size': len(subsample_indices), 'indices': subsample_indices, 'split_feature': None, 'split_value': None, 'left': None, 'right': None}
        threshold = np.random.uniform(min_val, max_val)
        left_mask = feature_values < threshold
        right_mask = ~left_mask
        left_indices = subsample_indices[left_mask]
        right_indices = subsample_indices[right_mask]
        left_subtree = build_tree(left_indices, depth + 1, height_limit)
        right_subtree = build_tree(right_indices, depth + 1, height_limit)
        return {'size': len(subsample_indices), 'indices': subsample_indices, 'split_feature': feature_idx, 'split_value': threshold, 'left': left_subtree, 'right': right_subtree}

    def path_length(tree, x_idx):
        node = tree
        length = 0
        while node['split_feature'] is not None:
            feature_idx = node['split_feature']
            threshold = node['split_value']
            if data[x_idx, feature_idx] < threshold:
                node = node['left']
            else:
                node = node['right']
            length += 1
        return length + c(node['size'])
    height_limit = math.ceil(math.log2(sample_size))
    trees = []
    for _ in range(n_trees):
        if sample_size >= n_samples:
            sample_indices = np.arange(n_samples)
        else:
            sample_indices = np.random.choice(n_samples, size=sample_size, replace=False)
        tree = build_tree(sample_indices, depth=0, height_limit=height_limit)
        trees.append(tree)
    path_lengths = np.zeros(n_samples)
    for i in range(n_samples):
        total_length = 0
        for tree in trees:
            total_length += path_length(tree, i)
        path_lengths[i] = total_length / n_trees
    phi = 2 * math.log(n_samples - 1) - 2 * (n_samples - 1) / n_samples
    scores = np.power(2, -(path_lengths / phi))
    if epsilon == 0:
        return []
    threshold = np.quantile(scores, 1 - epsilon)
    outlier_indices = np.where(scores >= threshold)[0]
    return sorted(outlier_indices.tolist())
