def pegasos_kernel_svm(data: np.ndarray, labels: np.ndarray, kernel='linear', lambda_val=0.01, iterations=100, sigma=1.0):
    (n_samples, n_features) = data.shape
    alpha = np.zeros(n_samples)
    bias = 0.0

    def kernel_function(x1, x2):
        if kernel == 'linear':
            return np.dot(x1, x2)
        elif kernel == 'rbf':
            return np.exp(-np.linalg.norm(x1 - x2) ** 2 / (2 * sigma ** 2))
        else:
            raise ValueError("Unsupported kernel type. Use 'linear' or 'rbf'.")
    for _ in range(iterations):
        decision_values = np.array([bias + np.sum((alpha[i] * labels[i] * kernel_function(data[i], data[j]) for i in range(n_samples))) for j in range(n_samples)])
        for j in range(n_samples):
            if labels[j] * decision_values[j] < 1:
                alpha[j] = (1 - lambda_val) * alpha[j] + 1 / (lambda_val * n_samples) * labels[j]
            else:
                alpha[j] = (1 - lambda_val) * alpha[j]
        bias = bias * (1 - lambda_val) + 1 / (lambda_val * n_samples) * np.sum(labels * (decision_values < 1))
    alpha = np.round(alpha, 4)
    bias = np.round(bias, 4)
    return (alpha.tolist(), bias)
