def rref(matrix):
    A = np.array(matrix, dtype=float)
    (rows, cols) = A.shape
    lead = 0
    for r in range(rows):
        if lead >= cols:
            return A.tolist()
        i = r
        while A[i, lead] == 0:
            i += 1
            if i == rows:
                i = r
                lead += 1
                if lead == cols:
                    return A.tolist()
                continue
        A[[r, i]] = A[[i, r]]
        A[r] = A[r] / A[r, lead]
        for i in range(rows):
            if i != r:
                A[i] = A[i] - A[i, lead] * A[r]
        lead += 1
    return A.tolist()
