def compute_tf_idf(corpus, query):
    if not corpus:
        return []
    num_docs = len(corpus)
    tf_list = []
    for doc in corpus:
        if not doc:
            tf_list.append({})
            continue
        tf_counter = Counter(doc)
        total_terms = len(doc)
        tf = {word: count / total_terms for (word, count) in tf_counter.items()}
        tf_list.append(tf)
    df_counter = Counter()
    for doc in corpus:
        unique_terms = set(doc)
        for term in unique_terms:
            df_counter[term] += 1
    idf = {}
    for term in query:
        df = df_counter.get(term, 0)
        idf[term] = math.log((num_docs + 1) / (df + 1)) + 1
    tf_idf_scores = []
    for tf in tf_list:
        scores = []
        for term in query:
            tf_value = tf.get(term, 0)
            tf_idf_value = tf_value * idf[term]
            scores.append(round(tf_idf_value, 4))
        tf_idf_scores.append(scores)
    return np.array(tf_idf_scores).tolist()
