def create_row_hv(row, dim, random_seeds):

    def generate_hypervector(seed):
        np.random.seed(seed)
        return np.random.choice([-1, 1], size=dim)
    composite_hv = np.zeros(dim)
    for (feature, value) in row.items():
        feature_hv = generate_hypervector(random_seeds[feature])
        value_hv = generate_hypervector(random_seeds[feature] + 1)
        bound_hv = feature_hv * value_hv
        composite_hv += bound_hv
    norm = np.linalg.norm(composite_hv)
    if norm > 0:
        composite_hv /= norm
    return composite_hv.tolist()
