import numpy as np
def load_encoder_hparams_and_params(model_size: str='124M', models_dir: str='models'):

    class DummyBPE:

        def __init__(self):
            self.encoder_dict = {'hello': 1, 'world': 2, '<UNK>': 0}

        def encode(self, text: str):
            tokens = text.strip().split()
            return [self.encoder_dict.get(token, self.encoder_dict['<UNK>']) for token in tokens]

        def decode(self, token_ids: list):
            reversed_dict = {v: k for (k, v) in self.encoder_dict.items()}
            return ' '.join([reversed_dict.get(tok_id, '<UNK>') for tok_id in token_ids])
    hparams = {'n_ctx': 1024, 'n_head': 12}
    params = {'wte': np.random.rand(3, 10), 'wpe': np.random.rand(1024, 10), 'blocks': [], 'ln_f': {'g': np.ones(10), 'b': np.zeros(10)}}
    encoder = DummyBPE()
    return (encoder, hparams, params)
def multi_head_attention(x, wq, wk, wv, n_heads):
    (batch_size, seq_length, d_model) = x.shape
    depth = d_model // n_heads
    x_heads = np.reshape(x, (batch_size, seq_length, n_heads, depth))
    x_heads = np.transpose(x_heads, (0, 2, 1, 3))
    queries = np.einsum('bhld,ldm->bhm', x_heads, wq)
    keys = np.einsum('bhld,ldm->bhm', x_heads, wk)
    values = np.einsum('bhld,ldm->bhm', x_heads, wv)
    scores = np.einsum('bhm,bhn->bhnm', queries, keys) / np.sqrt(depth)
    attention_weights = np.softmax(scores, axis=-1)
    output = np.einsum('bhnm,bhm->bhm', attention_weights, values)
    return output
def feed_forward(x, w1, b1, w2, b2):
    return np.einsum('bld,dm->blm', np.maximum(0, np.einsum('bld,dm->blm', x, w1) + b1), w2) + b2
def layer_norm(x, g, b):
    mean = np.mean(x, axis=-1, keepdims=True)
    variance = np.var(x, axis=-1, keepdims=True)
    normalized = (x - mean) / np.sqrt(variance + 1e-06)
    return g * normalized + b
def gen_text(prompt: str, n_tokens_to_generate: int=40):
    np.random.seed(42)
    (encoder, hparams, params) = load_encoder_hparams_and_params()
    input_ids = encoder.encode(prompt)
    input_ids = np.array(input_ids).reshape(1, -1)
    token_embeddings = params['wte'][input_ids]
    position_ids = np.arange(input_ids.shape[1]).reshape(1, -1)
    positional_embeddings = params['wpe'][position_ids]
    x = token_embeddings + positional_embeddings
    n_heads = hparams['n_head']
    d_model = params['wte'].shape[1]
    wq = np.random.rand(d_model, d_model)
    wk = np.random.rand(d_model, d_model)
    wv = np.random.rand(d_model, d_model)
    w1 = np.random.rand(d_model, d_model)
    b1 = np.random.rand(d_model)
    w2 = np.random.rand(d_model, d_model)
    b2 = np.random.rand(d_model)
    attn_output = multi_head_attention(x, wq, wk, wv, n_heads)
    ff_output = feed_forward(attn_output, w1, b1, w2, b2)
    x = layer_norm(ff_output + x, params['ln_f']['g'], params['ln_f']['b'])
    generated_tokens = []
    for _ in range(n_tokens_to_generate):
        next_token = np.argmax(x[:, -1, :])
        generated_tokens.append(next_token)
        input_ids = np.append(input_ids, [[next_token]], axis=1)
        token_embeddings = params['wte'][input_ids]
        position_ids = np.arange(input_ids.shape[1]).reshape(1, -1)
        positional_embeddings = params['wpe'][position_ids]
        x = token_embeddings + positional_embeddings
        attn_output = multi_head_attention(x, wq, wk, wv, n_heads)
        ff_output = feed_forward(attn_output, w1, b1, w2, b2)
        x = layer_norm(ff_output + x, params['ln_f']['g'], params['ln_f']['b'])
    generated_text = encoder.decode(generated_tokens)
    return generated_text
