import numpy as np
def glm_links():
    """Construct and return standard GLM link functions.

    Returns
    -------
    dict
        A three-entry dictionary (identity, log, logit) where each entry is a
        dictionary containing callables for the link, its inverse and its
        derivative with respect to μ. All functions must work with numpy
        scalars as well as 1-D/2-D numpy arrays via element-wise operations.
    """

    def identity_link(mu):
        return mu

    def identity_inv_link(eta):
        return eta

    def identity_derivative(mu):
        return np.ones_like(mu)

    def log_link(mu):
        return np.log(mu)

    def log_inv_link(eta):
        return np.exp(eta)

    def log_derivative(mu):
        return 1 / mu

    def logit_link(mu):
        return np.log(mu / (1 - mu))

    def logit_inv_link(eta):
        return 1 / (1 + np.exp(-eta))

    def logit_derivative(mu):
        return 1 / (mu * (1 - mu))
    return {'identity': {'link': identity_link, 'inv_link': identity_inv_link, 'derivative': identity_derivative}, 'log': {'link': log_link, 'inv_link': log_inv_link, 'derivative': log_derivative}, 'logit': {'link': logit_link, 'inv_link': logit_inv_link, 'derivative': logit_derivative}}
mu = np.array([0.2, 0.8])

def check_data(a, b):
    """Validate and convert the two inputs so that both are numeric.

    The function must accept integers, floats, or numeric strings. Any string
    should be converted with `float()`; existing numeric values stay as they
    are. The pair `(a, b)` must then be returned.
    """
    if isinstance(a, str):
        a = float(a)
    if isinstance(b, str):
        b = float(b)
    return (a, b)
def validate_input(function):

    def wrapper(a, b):
        (a, b) = check_data(a, b)
        return function(a, b)
    return wrapper
@validate_input
def sum_numbers(a, b):
    """Return the arithmetic sum of *a* and *b* after validation."""
    result = a + b
    return int(result) if result.is_integer() else result

from typing import List, Optional
class Node:
    """Binary decision-tree node (provided).

    Non-leaf node:   rule = (feature_index, threshold)
    Leaf node:       rule = (None, prediction_value)
    """

    def __init__(self, left: Optional['Node'], right: Optional['Node'], rule: tuple):
        self.left = left
        self.right = right
        self.feature = rule[0]
        self.threshold = rule[1]
def predict_tree(root: Optional[Node], X: List[List[float]]) -> List[float]:
    """Predict the output for a batch of samples using the decision tree."""
    if root is None:
        return []
    predictions = []
    for sample in X:
        current_node = root
        while current_node.left is not None or current_node.right is not None:
            if sample[current_node.feature] <= current_node.threshold:
                current_node = current_node.left
            else:
                current_node = current_node.right
        predictions.append(current_node.threshold)
    return predictions

from typing import List, Union
Number = Union[int, float]
def k_smallest(nums: List[Number], k: int) -> List[Number]:
    """Return the k smallest numbers contained in *nums*.

    The function should scan the input list once, maintaining a custom
    max-heap (size at most k) implemented with a Python list.  At the end,
    return the k smallest elements in ascending order.  If *k* is invalid
    (≤0 or larger than len(nums)) return -1.

    The use of the built-in ``heapq`` module is **not** allowed.
    """
    if k <= 0 or k > len(nums):
        return -1
    max_heap = []

    def _move_up(index: int):
        """Move the element at index up to restore the heap property."""
        while index > 0:
            parent_index = (index - 1) // 2
            if max_heap[index] > max_heap[parent_index]:
                (max_heap[index], max_heap[parent_index]) = (max_heap[parent_index], max_heap[index])
                index = parent_index
            else:
                break

    def _move_down(index: int):
        """Move the element at index down to restore the heap property."""
        size = len(max_heap)
        while index < size:
            left_child_index = 2 * index + 1
            right_child_index = 2 * index + 2
            largest_index = index
            if left_child_index < size and max_heap[left_child_index] > max_heap[largest_index]:
                largest_index = left_child_index
            if right_child_index < size and max_heap[right_child_index] > max_heap[largest_index]:
                largest_index = right_child_index
            if largest_index != index:
                (max_heap[index], max_heap[largest_index]) = (max_heap[largest_index], max_heap[index])
                index = largest_index
            else:
                break
    for num in nums:
        if len(max_heap) < k:
            max_heap.append(num)
            _move_up(len(max_heap) - 1)
        elif num < max_heap[0]:
            max_heap[0] = num
            _move_down(0)
    return sorted(max_heap)

import numpy as np
def newton(X: np.ndarray, y: np.ndarray, epsilon: float=1e-06, max_iter: int=1000) -> list[list[float]]:
    w = np.array([[1.0], [1.0]])
    n = X.shape[0]
    for _ in range(max_iter):
        residual = X @ w - y
        gradient = 2 / n * (X.T @ residual)
        if np.linalg.norm(gradient) < epsilon:
            break
        hessian = 2 / n * (X.T @ X)
        try:
            hessian_inv = np.linalg.inv(hessian)
        except np.linalg.LinAlgError:
            hessian_inv = np.linalg.pinv(hessian)
        w -= hessian_inv @ gradient
    return [[round(w[0, 0], 4)], [round(w[1, 0], 4)]]

import numpy as np
def softplus(x, deriv: bool=False):
    """Compute the SoftPlus activation function or its derivative.

    Args:
        x: A scalar, list, or NumPy array containing real values.
        deriv: A boolean flag indicating whether to return the derivative.

    Returns:
        A float if x is a scalar, or a list of floats if x is a list or array.
    """
    x = np.asarray(x)
    if deriv:
        result = 1 / (1 + np.exp(-x))
    else:
        result = np.where(x > 0, x + np.log1p(np.exp(-x)), np.log1p(np.exp(x)))
    result = np.round(result, 4)
    if np.isscalar(x):
        return float(result)
    else:
        return result.tolist()

import numpy as np
def compute_impurity(y, criterion):
    """Compute a node's impurity.

    Args:
        y (numpy.ndarray): 1-D array with the target values.
        criterion (str): One of {"entropy", "gini", "mse"}.

    Returns:
        float: Impurity value rounded to 4 decimal places.
    """
    if len(y) == 0:
        return 0.0
    if criterion == 'entropy':
        (value, counts) = np.unique(y, return_counts=True)
        probabilities = counts / counts.sum()
        entropy = -np.sum(probabilities * np.log2(probabilities + 1e-10))
        return round(entropy, 4)
    elif criterion == 'gini':
        (value, counts) = np.unique(y, return_counts=True)
        probabilities = counts / counts.sum()
        gini = 1 - np.sum(probabilities ** 2)
        return round(gini, 4)
    elif criterion == 'mse':
        mse = np.mean((y - np.mean(y)) ** 2)
        return round(mse, 4)
    else:
        raise ValueError("Criterion must be one of {'entropy', 'gini', 'mse'}")

import numpy as np
def forward_algorithm(S: list[float], A: list[list[float]], B: list[list[float]], observations: list[int]) -> float:
    """Forward algorithm for Hidden Markov Models.

    Args:
        S (list[float]): Initial state probabilities.
        A (list[list[float]]): State–transition probabilities.
        B (list[list[float]]): Emission probabilities.
        observations (list[int]): Observation index sequence.

    Returns:
        float: Sequence likelihood rounded to 4 decimals, or −1 on invalid input.
    """
    n = len(S)
    if n == 0 or len(A) != n or len(B) != n:
        return -1
    for row in A:
        if len(row) != n:
            return -1
    m = len(B[0]) if n > 0 else 0
    for row in B:
        if len(row) != m:
            return -1
    if len(observations) == 0:
        return -1
    for obs in observations:
        if obs < 0 or obs >= m:
            return -1
    alpha = np.zeros((len(observations), n))
    for i in range(n):
        alpha[0][i] = S[i] * B[i][observations[0]]
    for t in range(1, len(observations)):
        for j in range(n):
            alpha[t][j] = sum((alpha[t - 1][i] * A[i][j] for i in range(n))) * B[j][observations[t]]
    likelihood = sum((alpha[len(observations) - 1][j] for j in range(n)))
    return round(likelihood, 4)

from collections import Counter, defaultdict
class TreeNode:

    def __init__(self, item, count, parent=None):
        self.item = item
        self.count = count
        self.parent = parent
        self.children = {}
        self.node_link = None
def build_fp_tree(transactions, min_support):
    item_count = Counter((item for transaction in transactions for item in transaction))
    item_count = {item: count for (item, count) in item_count.items() if count >= min_support}
    if not item_count:
        return (None, None)
    sorted_items = [item for (item, _) in sorted(item_count.items(), key=lambda x: (-x[1], x[0]))]
    root = TreeNode('root', 0)
    for transaction in transactions:
        filtered_items = [item for item in sorted_items if item in transaction]
        if filtered_items:
            current_node = root
            for item in filtered_items:
                if item in current_node.children:
                    current_node.children[item].count += 1
                else:
                    new_node = TreeNode(item, 1, current_node)
                    current_node.children[item] = new_node
                current_node = current_node.children[item]
    header_table = {}
    for item in sorted_items:
        header_table[item] = None
    for (item, node) in header_table.items():
        if node is None:
            current_node = root
            while current_node:
                if item in current_node.children:
                    if header_table[item] is None:
                        header_table[item] = current_node.children[item]
                    else:
                        current_node.children[item].node_link = header_table[item]
                        header_table[item] = current_node.children[item]
                current_node = current_node.children.get(item, None)
    return (root, header_table)
def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth.

    A *transaction* is represented by a list of items (strings).  `min_support`
    is the minimum number of transactions an item-set has to appear in.  The
    function returns **all** frequent item-sets where
        support(itemset) >= min_support.

    The result must be deterministic:
      • Inside each item-set the items have to be sorted alphabetically.
      • The outer list has to be sorted by `(len(itemset), itemset)`.
    If *transactions* is empty or no item-set meets the threshold return an
    empty list.
    """
    if not transactions:
        return []
    (fp_tree, header_table) = build_fp_tree(transactions, min_support)
    frequent_itemsets = []
    if fp_tree is not None:
        for item in header_table.keys():
            prefix = [item]
            conditional_transactions = []
            current_node = header_table[item]
            while current_node is not None:
                count = current_node.count
                path = []
                parent = current_node.parent
                while parent and parent.item != 'root':
                    path.append(parent.item)
                    parent = parent.parent
                if path:
                    conditional_transactions.extend([path] * count)
                current_node = current_node.node_link
            (conditional_fp_tree, _) = build_fp_tree(conditional_transactions, min_support)
            mine_fp_tree(conditional_fp_tree, prefix, min_support, frequent_itemsets)
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets

import numpy as np
def relu_(Z):
    """Apply the element-wise Rectified Linear Unit (ReLU) activation.

    The function keeps the container type of the input:
    scalar → scalar, list → list, NumPy array → NumPy array.

    Args:
        Z (int | float | list | numpy.ndarray): Input data – scalar, 1-D or 2-D.

    Returns:
        Same type as *Z* with all negative values clipped to 0.
    """
    if isinstance(Z, np.ndarray):
        return np.maximum(0, Z)
    elif isinstance(Z, list):
        if all((isinstance(i, list) for i in Z)):
            return [relu_(row) for row in Z]
        else:
            return list(np.maximum(0, Z))
    elif isinstance(Z, (int, float)):
        return max(0, Z)
    else:
        raise TypeError('Input must be a scalar, list, or numpy.ndarray.')

import numpy as np
TIME_STEPS = 20
def string_to_int(text: str, time_steps: int, vocabulary: dict[str, int]) -> list[int]:
    """Encodes a string into a fixed-length list of integer token ids."""
    encoded = []
    for char in text:
        encoded.append(vocabulary.get(char, 0))
    if len(encoded) < time_steps:
        encoded += [0] * (time_steps - len(encoded))
    else:
        encoded = encoded[:time_steps]
    return encoded
def int_to_string(indices: list[int], inverse_vocab: dict[int, str]) -> str:
    """Converts a list of integer ids back to a human-readable string."""
    decoded = []
    for index in indices:
        if index != 0:
            decoded.append(inverse_vocab.get(index, ''))
    return ''.join(decoded)
def run_example(model, input_vocabulary: dict[str, int], inv_output_vocabulary: dict[int, str], text: str) -> str:
    """Encodes the input text, predicts using the model, and decodes the output."""
    encoded_input = string_to_int(text, TIME_STEPS, input_vocabulary)
    input_array = np.array(encoded_input).reshape(1, -1)
    predictions = model.predict(input_array)
    predicted_ids = np.argmax(predictions, axis=-1)[0]
    decoded_output = int_to_string(predicted_ids, inv_output_vocabulary)
    return decoded_output

