from collections import deque
def escape_fire_maze(grid: list[str]) -> int:
    n = len(grid)
    if grid[0][0] == 'F' or grid[n - 1][n - 1] == 'F':
        return -1
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    fire_time = [[float('inf')] * n for _ in range(n)]
    fire_queue = deque()
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                fire_queue.append((i, j))
                fire_time[i][j] = 0
    while fire_queue:
        (x, y) = fire_queue.popleft()
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n and (grid[nx][ny] == '.') and (fire_time[nx][ny] == float('inf')):
                fire_time[nx][ny] = fire_time[x][y] + 1
                fire_queue.append((nx, ny))
    agent_queue = deque([(0, 0, 0)])
    visited = [[False] * n for _ in range(n)]
    visited[0][0] = True
    while agent_queue:
        (x, y, time) = agent_queue.popleft()
        if x == n - 1 and y == n - 1:
            return time
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n and (not visited[nx][ny]):
                if grid[nx][ny] == '.' and time + 1 < fire_time[nx][ny]:
                    visited[nx][ny] = True
                    agent_queue.append((nx, ny, time + 1))
    return -1

import numpy as np
def knn_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int, metric: str='euclidean') -> np.ndarray:
    """Predict the class labels for the test set using k-NN classifier."""

    def euclidean_distance(a, b):
        return np.sqrt(np.sum((a - b) ** 2))

    def manhattan_distance(a, b):
        return np.sum(np.abs(a - b))

    def cosine_distance(a, b):
        return 1 - np.dot(a, b) / (np.linalg.norm(a) * np.linalg.norm(b))
    if metric == 'euclidean':
        distance_function = euclidean_distance
    elif metric == 'manhattan':
        distance_function = manhattan_distance
    elif metric == 'cosine':
        distance_function = cosine_distance
    else:
        distance_function = euclidean_distance
    predictions = []
    for test_sample in X_test:
        distances = np.array([distance_function(test_sample, train_sample) for train_sample in X_train])
        k_indices = np.argsort(distances)[:k]
        k_nearest_labels = y_train[k_indices]
        (unique_labels, counts) = np.unique(k_nearest_labels, return_counts=True)
        max_count_index = np.argmax(counts)
        if np.sum(counts == counts[max_count_index]) > 1:
            predicted_label = unique_labels[np.argmin(unique_labels[counts == counts[max_count_index]])]
        else:
            predicted_label = unique_labels[max_count_index]
        predictions.append(predicted_label)
    return np.array(predictions)

import numpy as np
def sigmoid(X: np.ndarray) -> np.ndarray:
    """Compute the sigmoid activation function."""
    return 1 / (1 + np.exp(-X))
def sigmoid_derivative(A: np.ndarray) -> np.ndarray:
    """Compute the derivative of the sigmoid function."""
    return A * (1 - A)
def tanh(X: np.ndarray) -> np.ndarray:
    """Compute the tanh activation function."""
    return np.tanh(X)
def tanh_derivative(A: np.ndarray) -> np.ndarray:
    """Compute the derivative of the tanh function."""
    return 1 - A ** 2
def relu(X: np.ndarray) -> np.ndarray:
    """Compute the ReLU activation function."""
    return np.maximum(0, X)
def relu_derivative(A: np.ndarray) -> np.ndarray:
    """Compute the derivative of the ReLU function."""
    return (A > 0).astype(A.dtype)
def softmax(X: np.ndarray) -> np.ndarray:
    """Compute the softmax activation function in a numerically stable way."""
    e_x = np.exp(X - np.max(X, axis=-1, keepdims=True))
    return e_x / np.sum(e_x, axis=-1, keepdims=True)
def activation_forward_backward(X: np.ndarray, activation: str, upstream_grad: np.ndarray) -> tuple[np.ndarray, np.ndarray]:
    """Apply an activation function and compute its local gradient.

    Parameters
    ----------
    X : np.ndarray
        Raw pre-activation values.
    activation : str
        Name of the activation function. Supported values are
        "sigmoid", "tanh", "relu", and "softmax".
    upstream_grad : np.ndarray
        Gradient that arrives from the layer above (must have the same shape as
        `X`).

    Returns
    -------
    tuple[np.ndarray, np.ndarray]
        A two-element tuple `(A, grad)` where:
          • `A`    – the activation applied to `X`.
          • `grad` – the element-wise product of `upstream_grad` and the local
                      derivative of the activation (rounded to 4 decimals).
    """
    if activation == 'sigmoid':
        A = sigmoid(X)
        grad = sigmoid_derivative(A)
    elif activation == 'tanh':
        A = tanh(X)
        grad = tanh_derivative(A)
    elif activation == 'relu':
        A = relu(X)
        grad = relu_derivative(A)
    elif activation == 'softmax':
        A = softmax(X)
        grad = np.ones_like(A)
    else:
        raise ValueError(f'Unsupported activation function: {activation}')
    grad = np.round(upstream_grad * grad, 4)
    A = np.round(A, 4)
    return (A, grad)

def accuracy_score(y_true, y_pred):
    """Return the classification accuracy between *y_true* and *y_pred*.

    The function must:
      • return ‑1 if the two containers have different lengths;
      • return 0 when both containers are empty;
      • otherwise return the proportion of matching elements rounded to
        4 decimal places.

    Args:
        y_true (list | tuple): Ground-truth class labels.
        y_pred (list | tuple): Predicted class labels.

    Returns:
        float | int: The accuracy or ‑1 according to the rules above.
    """
    if len(y_true) != len(y_pred):
        return -1
    if len(y_true) == 0:
        return 0.0
    correct_predictions = sum((1 for (true, pred) in zip(y_true, y_pred) if true == pred))
    accuracy = correct_predictions / len(y_true)
    return round(accuracy, 4)

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """

    def validate_vectors(x, y):
        """Validate that x and y are one-dimensional, non-empty, equal-length numeric iterables."""
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return False
        if len(x) != len(y) or len(x) == 0:
            return False
        try:
            x = np.asarray(x, dtype=float)
            y = np.asarray(y, dtype=float)
        except ValueError:
            return False
        if x.ndim != 1 or y.ndim != 1:
            return False
        return (x, y)

    def euclidean(x, y):
        return np.sqrt(np.sum((x - y) ** 2))

    def manhattan(x, y):
        return np.sum(np.abs(x - y))

    def chebyshev(x, y):
        return np.max(np.abs(x - y))

    def cosine(x, y):
        norm_x = np.linalg.norm(x)
        norm_y = np.linalg.norm(y)
        if norm_x == 0 or norm_y == 0:
            return -1
        return 1 - np.dot(x, y) / (norm_x * norm_y)

    def distance_function(x, y):
        validated = validate_vectors(x, y)
        if validated is False:
            return -1
        (x, y) = validated
        if name == 'euclidean':
            return round(euclidean(x, y), 4)
        elif name == 'manhattan':
            return round(manhattan(x, y), 4)
        elif name == 'chebyshev':
            return round(chebyshev(x, y), 4)
        elif name == 'cosine':
            return round(cosine(x, y), 4)
        else:
            return -1
    return distance_function

import numpy as np
def softsign(z):
    """Compute the Softsign activation function.

    Softsign(z) = z / (1 + |z|)

    The function accepts either a scalar or a list/NumPy array. For a scalar it
    returns a float; for an array-like input it returns a list of floats, with
    every element rounded to 4 decimal places.
    """
    if isinstance(z, (int, float)):
        result = z / (1 + abs(z))
        return round(result, 4)
    elif isinstance(z, (list, np.ndarray)):
        if len(z) == 0:
            return []
        result = [round(x / (1 + abs(x)), 4) for x in z]
        return result
    else:
        raise TypeError('Input must be a scalar, list, or NumPy array.')

import functools
from typing import Callable, Any
def doublewrap(decorator_func: Callable[..., Callable[..., Any]]) -> Callable[..., Any]:
    """Return a decorator that can be used *with* or *without* parentheses.

    Args:
        decorator_func: A callable that expects the function to decorate as
            its first positional argument followed only by *optional*
            positional or keyword arguments.

    Returns:
        A new decorator that forwards the call in the correct form so that
        ``decorator_func`` can be applied either directly or after receiving
        its own optional parameters.
    """

    @functools.wraps(decorator_func)
    def wrapper(*args, **kwargs):
        if len(args) == 1 and callable(args[0]):
            return decorator_func(args[0])
        else:
            return lambda func: decorator_func(func, *args, **kwargs)
    return wrapper

import numpy as np
def leaky_relu(z, a=0.01):
    """Apply the Leaky ReLU activation to every element in *z*.

    Args:
        z: A scalar, list (possibly nested) or ``numpy.ndarray`` of numbers.
        a: Optional float in [0,1) — the slope for negative inputs. Defaults to 0.01.

    Returns:
        A Python list with the same structure as *z* where each value has been transformed
        by the Leaky ReLU activation.
    """

    def apply_leaky_relu(x):
        return x if x >= 0 else a * x
    if isinstance(z, np.ndarray):
        return z.tolist() if a == 0.01 else np.where(z >= 0, z, a * z).tolist()
    elif isinstance(z, (int, float)):
        return apply_leaky_relu(z)
    elif isinstance(z, list):
        return [leaky_relu(item, a) for item in z]
    else:
        raise TypeError('Input must be a scalar, list, or numpy.ndarray.')

import numpy as np
def gaussian_naive_bayes(X_train, y_train, X_test):
    (n_samples, n_features) = X_train.shape
    means = {}
    variances = {}
    priors = {}
    for c in [0, 1]:
        X_c = X_train[y_train == c]
        means[c] = np.mean(X_c, axis=0)
        variances[c] = np.var(X_c, axis=0) + 1e-09
        priors[c] = X_c.shape[0] / n_samples

    def gaussian_log_pdf(x, mean, variance):
        return -0.5 * np.log(2 * np.pi * variance) - (x - mean) ** 2 / (2 * variance)
    predictions = []
    for x in X_test:
        log_posteriors = {}
        for c in [0, 1]:
            log_posterior = np.log(priors[c])
            log_likelihood = np.sum(gaussian_log_pdf(x, means[c], variances[c]))
            log_posterior += log_likelihood
            log_posteriors[c] = log_posterior
        predicted_class = max(log_posteriors, key=log_posteriors.get)
        predictions.append(predicted_class)
    return predictions

