import numpy as np
class L1Regularization:

    def __init__(self, alpha: float):
        """Initialize the L1Regularization with a given alpha.

        Args:
            alpha: Non-negative float controlling the strength of the regularisation.
        """
        if alpha < 0:
            raise ValueError('Alpha must be a non-negative float.')
        self.alpha = alpha

    def __call__(self, w: np.ndarray) -> float:
        """Calculate the L1 penalty value.

        Args:
            w: A NumPy array representing the weight vector.

        Returns:
            The L1 penalty value as a float.
        """
        if self.alpha == 0:
            return 0.0
        return self.alpha * np.sum(np.abs(w))

    def grad(self, w: np.ndarray) -> np.ndarray:
        """Calculate the gradient of the L1 penalty with respect to w.

        Args:
            w: A NumPy array representing the weight vector.

        Returns:
            A NumPy array representing the gradient of the penalty.
        """
        if self.alpha == 0:
            return np.zeros_like(w)
        return self.alpha * np.sign(w)

import numpy as np
def squared_error(actual, predicted):
    """Calculate the squared error or mean squared error.

    If *actual* and *predicted* are scalars, return the squared difference.
    If they are one-dimensional iterables (same length), return the mean squared
    error.  Return -1 for length mismatches or empty iterables.

    All results must be rounded to 4 decimal places.
    """
    if isinstance(actual, (int, float)) and isinstance(predicted, (int, float)):
        return round((actual - predicted) ** 2, 4)
    if isinstance(actual, (list, tuple, np.ndarray)) and isinstance(predicted, (list, tuple, np.ndarray)):
        if len(actual) != len(predicted) or len(actual) == 0:
            return -1
        mse = np.mean((np.array(actual) - np.array(predicted)) ** 2)
        return round(mse, 4)
    return -1

import heapq
from typing import List, Union
Number = Union[int, float]
def top_k_elements(sequence: List[Number], k: int, order: str='largest') -> List[Number]:
    """Keeps only the k largest or k smallest numbers from *sequence*.

    The function must visit each element exactly once and store no more than
    *k* items at any time by making use of the heapq module (binary heap).

    Args:
        sequence: A list of integers or floats representing the data stream.
        k: The number of extreme values to keep.
        order: "largest" to retain the k biggest values, "smallest" to retain
            the k smallest.

    Returns:
        A list with at most k numbers sorted according to *order*.
    """
    if k <= 0 or not sequence:
        return []
    if k >= len(sequence):
        return sorted(sequence, reverse=order == 'largest')
    if order == 'largest':
        min_heap = []
        for num in sequence:
            heapq.heappush(min_heap, num)
            if len(min_heap) > k:
                heapq.heappop(min_heap)
        return sorted(min_heap, reverse=True)
    elif order == 'smallest':
        max_heap = []
        for num in sequence:
            heapq.heappush(max_heap, -num)
            if len(max_heap) > k:
                heapq.heappop(max_heap)
        return sorted([-num for num in max_heap])
    else:
        raise ValueError("Order must be either 'largest' or 'smallest'.")

class Node:

    def __init__(self, key, val):
        self.key = key
        self.val = val
        self.left = None
        self.right = None

    def __lt__(self, other):
        return self.val < other.val

    def __le__(self, other):
        return self.val <= other.val

    def __gt__(self, other):
        return self.val > other.val

    def __ge__(self, other):
        return self.val >= other.val
def inorder_keys(pairs):
    """Build a BST from (key, value) *pairs* and return keys from an in-order traversal.

    Args:
        pairs (list[tuple[Any, int | float]]): List of (key, numeric value) pairs.

    Returns:
        list: Keys in in-order (ascending by numeric value). Return an empty
        list if *pairs* is empty.
    """
    if not pairs:
        return []
    root = Node(pairs[0][0], pairs[0][1])

    def insert(node, new_node):
        if new_node < node:
            if node.left is None:
                node.left = new_node
            else:
                insert(node.left, new_node)
        elif node.right is None:
            node.right = new_node
        else:
            insert(node.right, new_node)
    for (key, value) in pairs[1:]:
        new_node = Node(key, value)
        insert(root, new_node)

    def inorder_traversal(node):
        if node is None:
            return []
        return inorder_traversal(node.left) + [node.key] + inorder_traversal(node.right)
    return inorder_traversal(root)

import numpy as np
def get_loss(name: str):
    """Return a loss function specified by *name*.

    The function must recognise the following identifiers (case-insensitive,
    underscores are ignored):
        • MAE  →  "mae" | "mean_absolute_error"
        • MSE  →  "mse" | "mean_squared_error"
        • Binary Cross Entropy  →  "cross_entropy" | "log_loss" | "ce"

    The returned object has the signature `fn(y_true, y_pred) -> float` where
    *y_true* and *y_pred* are 1-D sequences (list or NumPy array) of equal
    length.  The resulting float must be rounded to **four** decimal places.

    Args:
        name: Name or alias of the desired loss function.

    Returns:
        A Python callable implementing the requested loss.

    Raises:
        ValueError: If *name* does not correspond to a supported loss.
    """
    normalized_name = name.replace('_', '').lower()

    def mae(y_true, y_pred):
        return round(np.mean(np.abs(np.array(y_true) - np.array(y_pred))), 4)

    def mse(y_true, y_pred):
        return round(np.mean((np.array(y_true) - np.array(y_pred)) ** 2), 4)

    def binary_cross_entropy(y_true, y_pred):
        y_pred_clipped = np.clip(np.array(y_pred), 1e-15, 1 - 1e-15)
        return round(-np.mean(np.array(y_true) * np.log(y_pred_clipped) + (1 - np.array(y_true)) * np.log(1 - y_pred_clipped)), 4)
    loss_functions = {'mae': mae, 'meanabsoluteerror': mae, 'mse': mse, 'meansquarederror': mse, 'crossentropy': binary_cross_entropy, 'logloss': binary_cross_entropy, 'ce': binary_cross_entropy}
    if normalized_name in loss_functions:
        return loss_functions[normalized_name]
    else:
        raise ValueError('Invalid loss function.')

def update_kernel_settings(parameters: dict, hyperparameters: dict, summary_dict: dict) -> tuple[dict, dict]:
    """Update kernel parameters and hyperparameters.

    The function must not modify the input dictionaries.  It should return two
    *new* dictionaries in which the supplied `summary_dict` updates have been
    applied following the rules explained in the task description.

    Args:
        parameters (dict):      Existing kernel parameters.
        hyperparameters (dict): Existing kernel hyperparameters.
        summary_dict (dict):    Dictionary that may contain the keys
            "parameters" and/or "hyperparameters" whose values are dictionaries
            of updates.  Any top-level keys are also treated as updates.

    Returns:
        tuple[dict, dict]: `(new_parameters, new_hyperparameters)` – the
        updated dictionaries.
    """
    new_parameters = parameters.copy()
    new_hyperparameters = hyperparameters.copy()
    updates = {}
    if 'parameters' in summary_dict:
        updates.update(summary_dict['parameters'])
    if 'hyperparameters' in summary_dict:
        updates.update(summary_dict['hyperparameters'])
    for (key, value) in updates.items():
        if key in new_parameters:
            new_parameters[key] = value
        elif key in new_hyperparameters:
            new_hyperparameters[key] = value
    return (new_parameters, new_hyperparameters)
