import numpy as np
def get_initializer(name: str):
    """Return a weight-initialisation callable associated with *name*.

    The callable must have the signature
        initializer(shape: tuple[int, ...], scale: float = 1.0) -> np.ndarray
    and create a numpy array filled according to the requested strategy.

    Supported strategies (case-insensitive):
        - "zeros"
        - "ones"
        - "normal"
        - "glorot_uniform"

    If *name* is not recognised the function must raise `ValueError`.
    """
    name = name.lower()
    if name == 'zeros':

        def initializer(shape: tuple[int, ...], scale: float=1.0) -> np.ndarray:
            return np.zeros(shape)
        return initializer
    elif name == 'ones':

        def initializer(shape: tuple[int, ...], scale: float=1.0) -> np.ndarray:
            return np.ones(shape) * scale
        return initializer
    elif name == 'normal':

        def initializer(shape: tuple[int, ...], scale: float=1.0) -> np.ndarray:
            return np.random.normal(0, scale, size=shape)
        return initializer
    elif name == 'glorot_uniform':

        def initializer(shape: tuple[int, ...], scale: float=1.0) -> np.ndarray:
            if len(shape) != 2:
                raise ValueError('Shape must be 2-dimensional for glorot_uniform.')
            (fan_in, fan_out) = shape
            limit = scale * np.sqrt(6 / (fan_in + fan_out))
            return np.random.uniform(-limit, limit, size=shape)
        return initializer
    else:
        raise ValueError(f'Unsupported initializer name: {name}')

import heapq
from collections import defaultdict
class Node:

    def __init__(self, token, frequency):
        self.token = token
        self.frequency = frequency

    def __lt__(self, other):
        if self.frequency == other.frequency:
            return self.token < other.token
        return self.frequency < other.frequency
def huffman_encode(tokens: list[str]) -> dict[str, str]:
    """Generate a Huffman code for the supplied tokens.

    The function must return a dictionary mapping **every** unique token in
    *tokens* to its binary Huffman code.  All codes must be prefix-free and
    consist only of the characters ‘0’ and ‘1’.
    """
    if not tokens:
        return {}
    frequency = defaultdict(int)
    for token in tokens:
        frequency[token] += 1
    heap = []
    for (token, freq) in frequency.items():
        heapq.heappush(heap, Node(token, freq))
    if len(heap) == 1:
        return {heap[0].token: '0'}
    while len(heap) > 1:
        left = heapq.heappop(heap)
        right = heapq.heappop(heap)
        merged = Node('', left.frequency + right.frequency)
        heapq.heappush(heap, merged)
    root = heap[0]
    huffman_codes = {}

    def generate_codes(node, current_code):
        if node.token:
            huffman_codes[node.token] = current_code
            return
        generate_codes(node.left, current_code + '0')
        generate_codes(node.right, current_code + '1')

    def build_tree(node):
        if len(heap) == 0:
            return
        left = heapq.heappop(heap)
        if len(heap) == 0:
            return left
        right = heapq.heappop(heap)
        merged = Node('', left.frequency + right.frequency)
        merged.left = left
        merged.right = right
        heapq.heappush(heap, merged)
        return merged
    while len(heap) > 1:
        build_tree(heap)
    generate_codes(root, '')
    return huffman_codes

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool) -> PyTuple[bool, bool]:
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def is_space_continuous(space: Space) -> bool:
        if isinstance(space, Box):
            return True
        elif isinstance(space, Tuple):
            return all((is_space_continuous(sub_space) for sub_space in space.spaces))
        elif isinstance(space, Dict):
            return all((is_space_continuous(sub_space) for sub_space in space.spaces.values()))
        else:
            return False
    if tuple_action:
        cont_action = is_space_continuous(env.action_space)
    else:
        cont_action = isinstance(env.action_space, Box)
    if tuple_obs:
        cont_obs = is_space_continuous(env.observation_space)
    else:
        cont_obs = isinstance(env.observation_space, Box)
    return (cont_action, cont_obs)

import re
from typing import Any, Dict, Optional
class KernelBase:
    """Minimal base class for all kernels.  Do NOT modify this class."""

    def __init__(self, **kwargs: Any) -> None:
        self.params: Dict[str, Any] = kwargs

    def set_params(self, state: Dict[str, Any]):
        """(Re)sets parameters from a previously stored summary dict."""
        hp = state.get('hyperparameters', {})
        self.params.update({k: v for (k, v) in hp.items() if k != 'id'})
        return self

    def summary(self) -> Dict[str, Any]:
        """Dictionary description that can be fed back to *initialize_kernel*."""
        return {'hyperparameters': {'id': self.__class__.__name__, **self.params}}
class LinearKernel(KernelBase):

    def __init__(self):
        super().__init__()
class PolynomialKernel(KernelBase):

    def __init__(self, degree: int=3, coef0: float=1.0):
        super().__init__(degree=degree, coef0=coef0)
class RBFKernel(KernelBase):

    def __init__(self, gamma: float=1.0):
        super().__init__(gamma=gamma)
def parse_kernel_string(kernel_str: str) -> tuple:
    """Parse a kernel string and return the kernel class and its parameters."""
    kernel_str = kernel_str.strip().lower()
    if kernel_str.startswith('linear'):
        return (LinearKernel, {})
    elif kernel_str.startswith('polynomial'):
        match = re.match('polynomial\\((.*?)\\)', kernel_str)
        if match:
            params = match.group(1)
            kwargs = parse_params(params)
            return (PolynomialKernel, kwargs)
    elif kernel_str.startswith('rbf'):
        match = re.match('rbf\\((.*?)\\)', kernel_str)
        if match:
            params = match.group(1)
            kwargs = parse_params(params)
            return (RBFKernel, kwargs)
    else:
        raise NotImplementedError(f"Kernel '{kernel_str}' is not implemented.")
def parse_params(param_str: str) -> Dict[str, Any]:
    """Parse a parameter string into a dictionary of parameters."""
    params = {}
    for param in param_str.split(','):
        (key, value) = param.split('=')
        key = key.strip()
        value = value.strip()
        if value.isdigit():
            value = int(value)
        else:
            try:
                value = float(value)
            except ValueError:
                if value.lower() in ['true', 'false']:
                    value = value.lower() == 'true'
        params[key] = value
    return params
def initialize_kernel(param: Optional[Any]=None) -> KernelBase:
    """Create and return a kernel object from various representations.

    Args:
        param: None (default LinearKernel), an existing KernelBase instance, a
            string description such as 'RBF(gamma=0.5)', or a dictionary with a
            'hyperparameters' entry as produced by KernelBase.summary().

    Returns:
        KernelBase: the newly created or forwarded kernel instance.
    """
    if param is None:
        return LinearKernel()
    if isinstance(param, KernelBase):
        return param
    if isinstance(param, str):
        (kernel_class, kwargs) = parse_kernel_string(param)
        return kernel_class(**kwargs)
    if isinstance(param, dict):
        if 'hyperparameters' not in param:
            raise ValueError("The dictionary must contain a 'hyperparameters' key.")
        hyperparams = param['hyperparameters']
        kernel_id = hyperparams.get('id')
        if kernel_id == 'LinearKernel':
            return LinearKernel()
        elif kernel_id == 'PolynomialKernel':
            return PolynomialKernel(**{k: v for (k, v) in hyperparams.items() if k != 'id'})
        elif kernel_id == 'RBFKernel':
            return RBFKernel(**{k: v for (k, v) in hyperparams.items() if k != 'id'})
        else:
            raise NotImplementedError(f"Kernel '{kernel_id}' is not implemented.")
    raise ValueError('Invalid parameter type for kernel initialization.')

import numpy as np
class TreeNode:

    def __init__(self, value=None, feature_index=None, threshold=None, left=None, right=None):
        self.value = value
        self.feature_index = feature_index
        self.threshold = threshold
        self.left = left
        self.right = right
class RegressionTree:

    def __init__(self, max_depth):
        self.max_depth = max_depth
        self.root = None

    def fit(self, X, y):
        self.root = self._grow_tree(X, y, depth=0)

    def _grow_tree(self, X, y, depth):
        (n_samples, n_features) = X.shape
        if depth < self.max_depth and n_samples > 1:
            best_split = self._best_split(X, y)
            if best_split is not None:
                (left_indices, right_indices) = best_split['indices']
                left_node = self._grow_tree(X[left_indices], y[left_indices], depth + 1)
                right_node = self._grow_tree(X[right_indices], y[right_indices], depth + 1)
                return TreeNode(feature_index=best_split['feature_index'], threshold=best_split['threshold'], left=left_node, right=right_node)
        return TreeNode(value=np.mean(y))

    def _best_split(self, X, y):
        best_mse = float('inf')
        best_split = None
        (n_samples, n_features) = X.shape
        for feature_index in range(n_features):
            thresholds = np.unique(X[:, feature_index])
            for threshold in thresholds:
                left_indices = np.where(X[:, feature_index] < threshold)[0]
                right_indices = np.where(X[:, feature_index] >= threshold)[0]
                if len(left_indices) == 0 or len(right_indices) == 0:
                    continue
                left_target = y[left_indices]
                right_target = y[right_indices]
                mse = (np.mean((left_target - np.mean(left_target)) ** 2) * len(left_target) + np.mean((right_target - np.mean(right_target)) ** 2) * len(right_target)) / n_samples
                if mse < best_mse:
                    best_mse = mse
                    best_split = {'feature_index': feature_index, 'threshold': threshold, 'indices': (left_indices, right_indices)}
        return best_split

    def predict(self, X):
        return np.array([self._traverse_tree(x, self.root) for x in X])

    def _traverse_tree(self, x, node):
        if node.value is not None:
            return node.value
        if x[node.feature_index] < node.threshold:
            return self._traverse_tree(x, node.left)
        else:
            return self._traverse_tree(x, node.right)
def gbdt_regression(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_estimators: int=10, learning_rate: float=0.1, max_depth: int=3) -> list[float]:
    """Gradient Boosting Decision Tree (GBDT) regressor.

    Parameters
    ----------
    X_train : np.ndarray
        2-D array with shape (n_samples, n_features) containing the training
        features.
    y_train : np.ndarray
        1-D array of length n_samples containing the training targets.
    X_test : np.ndarray
        2-D array with shape (m_samples, n_features) containing the test
        features to predict.
    n_estimators : int, default=10
        Number of boosting iterations.
    learning_rate : float, default=0.1
        Shrinkage factor applied to each tree’s prediction.
    max_depth : int, default=3
        Maximum depth of every individual regression tree.

    Returns
    -------
    list[float]
        Predictions for every sample in *X_test*, rounded to 4 decimal places.
    """
    y_pred = np.full(y_train.shape, np.mean(y_train))
    for _ in range(n_estimators):
        residuals = y_train - y_pred
        tree = RegressionTree(max_depth=max_depth)
        tree.fit(X_train, residuals)
        tree_predictions = tree.predict(X_train)
        y_pred += learning_rate * tree_predictions
    final_predictions = tree.predict(X_test)
    return list(np.round(final_predictions, 4))

import numpy as np
def dct2(frame: list[int | float]) -> list[float]:
    """Implement the un-normalised 1-D DCT-II described in the task."""
    if not frame:
        return []
    N = len(frame)
    X = np.zeros(N)
    for k in range(N):
        sum_value = 0
        for n in range(N):
            sum_value += frame[n] * np.cos(np.pi / N * (n + 0.5) * k)
        X[k] = sum_value
    return [round(x, 4) for x in X]

import numpy as np
def root_mean_squared_log_error(actual: list[float], predicted: list[float]) -> float:
    """Calculate the Root Mean Squared Logarithmic Error (RMSLE) between actual and predicted values."""
    if len(actual) != len(predicted) or any((x < 0 for x in actual)) or any((x < 0 for x in predicted)):
        return -1
    n = len(actual)
    log_diff = np.log1p(predicted) - np.log1p(actual)
    rmsle = np.sqrt(np.mean(log_diff ** 2))
    return round(rmsle, 4)

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    x = np.asarray(x)
    sigmoid_value = 1 / (1 + np.exp(-x))
    if order == 0:
        result = sigmoid_value
    elif order == 1:
        result = sigmoid_value * (1 - sigmoid_value)
    elif order == 2:
        result = sigmoid_value * (1 - sigmoid_value) * (1 - 2 * sigmoid_value)
    else:
        return -1
    result = np.round(result, 4)
    if np.isscalar(x):
        return float(result)
    else:
        return result.tolist()

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    X = np.array(X)
    if Y is not None:
        Y = np.array(Y)
    (n_samples_X, n_features) = X.shape
    if Y is not None:
        (n_samples_Y, n_features_Y) = Y.shape
        if n_features != n_features_Y:
            return -1
    if sigma is None:
        sigma = np.sqrt(n_features / 2)
    elif isinstance(sigma, (float, int)):
        if sigma <= 0:
            return -1
        sigma = np.full(n_features, sigma)
    elif isinstance(sigma, list) or isinstance(sigma, np.ndarray):
        sigma = np.array(sigma)
        if sigma.shape[0] != n_features or np.any(sigma <= 0):
            return -1
    else:
        return -1
    if Y is None:
        Y = X
    K = np.zeros((n_samples_X, Y.shape[0]))
    for i in range(n_samples_X):
        for j in range(Y.shape[0]):
            diff = (X[i] - Y[j]) / sigma
            K[i, j] = np.exp(-0.5 * np.sum(diff ** 2))
    return K.round(4).tolist()
