import numpy as np
def regression_tree_predict(X_train: list[float | int], y_train: list[float | int], X_test: list[float | int], max_depth: int=3) -> list[float]:
    """Fit a univariate regression tree of depth *max_depth* and predict.

    Parameters
    ----------
    X_train : list[float | int]
        1-D feature values for the training set.
    y_train : list[float | int]
        Continuous target values for the training set.
    X_test : list[float | int]
        1-D feature values for which predictions are required.
    max_depth : int, default 3
        Maximum depth of the binary tree (root has depth 0).

    Returns
    -------
    list[float]
        Predictions for every element in *X_test*.
    """
    if not X_train or not y_train:
        return []

    class Node:

        def __init__(self, value=None, left=None, right=None):
            self.value = value
            self.left = left
            self.right = right

    def sse(y):
        """Calculate the sum of squared errors."""
        mean_y = np.mean(y)
        return np.sum((y - mean_y) ** 2)

    def best_split(X, y):
        """Find the best split point for the data."""
        best_sse = float('inf')
        best_value = None
        best_left_y = None
        best_right_y = None
        sorted_indices = np.argsort(X)
        X_sorted = np.array(X)[sorted_indices]
        y_sorted = np.array(y)[sorted_indices]
        for i in range(1, len(y_sorted)):
            if X_sorted[i] != X_sorted[i - 1]:
                left_y = y_sorted[:i]
                right_y = y_sorted[i:]
                current_sse = sse(left_y) + sse(right_y)
                if current_sse < best_sse:
                    best_sse = current_sse
                    best_value = (X_sorted[i] + X_sorted[i - 1]) / 2
                    best_left_y = left_y
                    best_right_y = right_y
        return (best_value, best_left_y, best_right_y)

    def build_tree(X, y, depth):
        """Recursively build the regression tree."""
        if depth >= max_depth or len(y) < 2:
            return Node(value=np.mean(y))
        (split_value, left_y, right_y) = best_split(X, y)
        if left_y is None or right_y is None:
            return Node(value=np.mean(y))
        left_indices = np.where(X < split_value)[0]
        right_indices = np.where(X >= split_value)[0]
        left_node = build_tree(X[left_indices], y[left_indices], depth + 1)
        right_node = build_tree(X[right_indices], y[right_indices], depth + 1)
        return Node(value=None, left=left_node, right=right_node)

    def predict_tree(node, x):
        """Predict using the regression tree."""
        if node.value is not None:
            return node.value
        if x < node.left.value:
            return predict_tree(node.left, x)
        else:
            return predict_tree(node.right, x)
    root = build_tree(X_train, y_train, 0)
    predictions = [predict_tree(root, x) for x in X_test]
    return predictions

