import itertools
class FPNode:

    def __init__(self, item, count, parent=None):
        self.item = item
        self.count = count
        self.parent = parent
        self.children = {}
        self.link = None
def build_fp_tree(transactions, min_sup):
    item_count = Counter()
    for transaction in transactions:
        item_count.update(set(transaction))
    frequent_items = {item: count for (item, count) in item_count.items() if count >= min_sup}
    if not frequent_items:
        return (None, None)
    sorted_items = sorted(frequent_items.items(), key=lambda x: (-x[1], x[0]))
    item_order = [item for (item, _) in sorted_items]
    root = FPNode('root', 0)
    header_table = {}
    for transaction in transactions:
        filtered_items = [item for item in item_order if item in transaction]
        if not filtered_items:
            continue
        current_node = root
        for item in filtered_items:
            if item in current_node.children:
                current_node.children[item].count += 1
            else:
                new_node = FPNode(item, 1, current_node)
                current_node.children[item] = new_node
                if item not in header_table:
                    header_table[item] = new_node
                else:
                    current_link = header_table[item]
                    while current_link.link is not None:
                        current_link = current_link.link
                    current_link.link = new_node
            current_node = current_node.children[item]
            current_node.count += 1
    return (root, header_table)
def mine_fp_tree(node, prefix, min_sup, header_table, result):
    if node is None:
        return
    if node.item != 'root' and node.count >= min_sup:
        new_prefix = prefix + [node.item]
        result.append(tuple(sorted(new_prefix)))
        conditional_base = []
        current = header_table[node.item]
        while current is not None:
            path = []
            parent = current.parent
            while parent and parent.item != 'root':
                path.append(parent.item)
                parent = parent.parent
            if path:
                conditional_base.extend([path] * current.count)
            current = current.link
        (conditional_tree, conditional_header) = build_fp_tree(conditional_base, min_sup)
        mine_fp_tree(conditional_tree, new_prefix, min_sup, conditional_header, result)
    next_node = header_table.get(node.item)
    while next_node is not None:
        mine_fp_tree(next_node, prefix, min_sup, header_table, result)
        next_node = next_node.link
def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """
    (root, header_table) = build_fp_tree(transactions, min_sup)
    result = []
    mine_fp_tree(root, [], min_sup, header_table, result)
    result = sorted(result, key=lambda x: (len(x), x))
    return result
