from typing import List, Union
def extract_archive(file_path: str, path: str='.', archive_format: Union[str, List[str], None]='auto') -> bool:
    """Checks whether *file_path* belongs to an allowed archive format.

    The function MUST NOT raise any exception and MUST NOT perform real file
    extraction.  Read the task description for the full specification.

    Args:
        file_path: Full path (or name) of the archive file.
        path: Destination folder – ignored in this simplified version.
        archive_format: See task description.

    Returns:
        True if the file name matches one of the allowed formats, False otherwise.
    """
    tar_extensions = {'.tar', '.tar.gz', '.tgz', '.tar.bz', '.tar.bz2', '.tbz'}
    zip_extensions = {'.zip'}
    (_, ext) = os.path.splitext(file_path)
    ext = ext.lower()
    if archive_format is None or (isinstance(archive_format, list) and (not archive_format)):
        return False
    if archive_format == 'auto':
        allowed_formats = ['tar', 'zip']
    elif isinstance(archive_format, str):
        allowed_formats = [archive_format]
    else:
        allowed_formats = archive_format
    if 'tar' in allowed_formats and ext in tar_extensions:
        return True
    if 'zip' in allowed_formats and ext in zip_extensions:
        return True
    return False

import numpy as np
def rmsprop_update(params: list[np.ndarray], grads: list[np.ndarray], s: list[np.ndarray] | None=None, alpha: float=0.01, beta: float=0.9, epsilon: float=1e-08) -> tuple[list[np.ndarray], list[np.ndarray]]:
    """Perform a single optimization step using the RMSProp algorithm."""
    if s is None or len(s) == 0:
        s = [np.zeros_like(g) for g in grads]
    new_params = []
    new_s = []
    for (param, grad, s_i) in zip(params, grads, s):
        s_i = beta * s_i + (1 - beta) * grad ** 2
        param_update = param - alpha * (grad / (np.sqrt(s_i) + epsilon))
        new_params.append(np.round(param_update, 6))
        new_s.append(s_i)
    return (new_params, new_s)

from collections import Counter
from typing import Any, Dict, List
def gini_impurity(data: List[List[Any]]) -> float:
    """Calculate the Gini impurity of a dataset."""
    if not data:
        return 0.0
    label_counts = Counter((sample[-1] for sample in data))
    total_samples = len(data)
    impurity = 1.0
    for count in label_counts.values():
        prob = count / total_samples
        impurity -= prob ** 2
    return impurity
def majority_class(data: List[List[Any]]) -> Any:
    """Return the majority class label from the dataset."""
    if not data:
        return None
    label_counts = Counter((sample[-1] for sample in data))
    return label_counts.most_common(1)[0][0]
def split_data(data: List[List[Any]], feature_index: int, value: Any) -> (List[List[Any]], List[List[Any]]):
    """Split the dataset into left and right subsets based on the feature index and value."""
    left = [sample for sample in data if sample[feature_index] == value]
    right = [sample for sample in data if sample[feature_index] != value]
    return (left, right)
def build_tree(data: List[List[Any]]) -> Dict[str, Any]:
    """Build a CART decision tree using the Gini impurity criterion."""
    if not data:
        return {}
    current_impurity = gini_impurity(data)
    best_gain = 0
    best_split = None
    best_left = None
    best_right = None
    num_features = len(data[0]) - 1
    for feature_index in range(num_features):
        unique_values = set((sample[feature_index] for sample in data))
        for value in unique_values:
            (left, right) = split_data(data, feature_index, value)
            if not left or not right:
                continue
            p_left = len(left) / len(data)
            p_right = len(right) / len(data)
            weighted_impurity = p_left * gini_impurity(left) + p_right * gini_impurity(right)
            gain = current_impurity - weighted_impurity
            if gain > best_gain:
                best_gain = gain
                best_split = (feature_index, value)
                best_left = left
                best_right = right
    if best_gain <= 0:
        return {'label': majority_class(data)}
    (feature_index, value) = best_split
    left_subtree = build_tree(best_left)
    right_subtree = build_tree(best_right)
    return {'feature': feature_index, 'value': value, 'left': left_subtree, 'right': right_subtree}
