def transpose_matrix(a: list[list[int | float]]) -> list[list[int | float]]:
    if not a or not a[0]:
        return []
    num_rows = len(a)
    num_cols = len(a[0])
    transposed = [[0] * num_rows for _ in range(num_cols)]
    for i in range(num_rows):
        for j in range(num_cols):
            transposed[j][i] = a[i][j]
    return transposed
