import numpy as np
def linear_kernel(x1, x2):
    return np.dot(x1, x2)
def rbf_kernel(x1, x2, sigma):
    return np.exp(-np.linalg.norm(x1 - x2) ** 2 / (2 * sigma ** 2))
def compute_kernel_matrix(data, kernel, sigma):
    n_samples = data.shape[0]
    kernel_matrix = np.zeros((n_samples, n_samples))
    for i in range(n_samples):
        for j in range(n_samples):
            if kernel == 'linear':
                kernel_matrix[i, j] = linear_kernel(data[i], data[j])
            elif kernel == 'rbf':
                kernel_matrix[i, j] = rbf_kernel(data[i], data[j], sigma)
    return kernel_matrix
def pegasos_kernel_svm(data: np.ndarray, labels: np.ndarray, kernel='linear', lambda_val=0.01, iterations=100, sigma=1.0):
    (n_samples, n_features) = data.shape
    alpha = np.zeros(n_samples)
    bias = 0.0
    kernel_matrix = compute_kernel_matrix(data, kernel, sigma)
    for t in range(1, iterations + 1):
        learning_rate = 1 / (lambda_val * t)
        for i in range(n_samples):
            margin = labels[i] * (np.dot(alpha * labels, kernel_matrix[i]) + bias)
            if margin < 1:
                alpha[i] = (1 - learning_rate * lambda_val) * alpha[i] + learning_rate * labels[i]
            else:
                alpha[i] = (1 - learning_rate * lambda_val) * alpha[i]
        bias = np.mean(labels - np.dot(alpha * labels, kernel_matrix))
    alpha = np.round(alpha, 4)
    bias = np.round(bias, 4)
    return (alpha.tolist(), bias)
