def rref(matrix):
    A = np.array(matrix, dtype=float)
    (rows, cols) = A.shape
    lead = 0
    for r in range(rows):
        if lead >= cols:
            return A.tolist()
        i = r
        while A[i, lead] == 0:
            i += 1
            if i == rows:
                i = r
                lead += 1
                if cols == lead:
                    return A.tolist()
        A[[i, r]] = A[[r, i]]
        lv = A[r, lead]
        A[r] = A[r] / lv
        for i in range(rows):
            if i != r:
                lv = A[i, lead]
                A[i] = A[i] - lv * A[r]
        lead += 1
    return A.tolist()
