def compute_tf_idf(corpus, query):
    if not corpus:
        return []
    doc_count = len(corpus)
    df = {term: 0 for term in query}
    for document in corpus:
        unique_terms = set(document)
        for term in query:
            if term in unique_terms:
                df[term] += 1
    idf = {term: np.log((doc_count + 1) / (df[term] + 1)) + 1 for term in query}
    tf_idf_scores = []
    for document in corpus:
        doc_length = len(document)
        tf_idf_doc = []
        for term in query:
            tf = document.count(term) / doc_length if doc_length > 0 else 0
            tf_idf = tf * idf[term]
            tf_idf_doc.append(round(tf_idf, 4))
        tf_idf_scores.append(tf_idf_doc)
    return np.array(tf_idf_scores).tolist()
