def calculate_brightness(img):
    if not img or not isinstance(img, list) or (not all((isinstance(row, list) for row in img))):
        return -1
    row_length = len(img[0])
    if any((len(row) != row_length for row in img)):
        return -1
    total_brightness = 0
    num_pixels = 0
    for row in img:
        for pixel in row:
            if not isinstance(pixel, int) or pixel < 0 or pixel > 255:
                return -1
            total_brightness += pixel
            num_pixels += 1
    if num_pixels == 0:
        return -1
    average_brightness = total_brightness / num_pixels
    return round(average_brightness, 2)
