import numpy as np
def create_random_hv(dim, seed):
    """Generate a random hypervector of given dimensionality using a seed."""
    np.random.seed(seed)
    return np.random.choice([-1, 1], size=dim)
def bind_hvs(hv1, hv2):
    """Bind two hypervectors using element-wise multiplication."""
    return hv1 * hv2
def bundle_hvs(hvs):
    """Bundle a list of hypervectors using element-wise summation and thresholding."""
    sum_hv = np.sum(hvs, axis=0)
    return np.where(sum_hv >= 0, 1, -1)
def create_row_hv(row, dim, random_seeds):
    """Create a composite hypervector for a dataset row."""
    bound_hvs = []
    for (feature, value) in row.items():
        feature_hv = create_random_hv(dim, random_seeds[feature])
        value_hv = create_random_hv(dim, random_seeds[feature] + hash(value))
        bound_hv = bind_hvs(feature_hv, value_hv)
        bound_hvs.append(bound_hv)
    composite_hv = bundle_hvs(bound_hvs)
    return composite_hv.tolist()
