def softplus(x: float) -> float:
    """
    Compute the softplus activation function.

    Args:
        x: Input value

    Returns:
        The softplus value: log(1 + e^x)
    """
    if x > 709:
        return round(x, 4)
    elif x < -745:
        return round(math.exp(x), 4)
    else:
        return round(math.log1p(math.exp(x)), 4)
