def softplus(x, deriv: bool=False):
    x_array = np.array(x, dtype=np.float64)
    if deriv:
        result = 1 / (1 + np.exp(-x_array))
    else:
        result = np.where(x_array > 0, np.log1p(np.exp(-x_array)) + x_array, np.log1p(np.exp(x_array)))
    rounded_result = np.round(result, 4)
    if np.isscalar(x):
        return float(rounded_result)
    return rounded_result.tolist()

