def build_fp_tree(transactions: List[List[str]], min_support: int) -> (FPTreeNode, Dict[str, FPTreeNode]):
    item_counter = Counter()
    for transaction in transactions:
        item_counter.update(transaction)
    frequent_items = {item for (item, count) in item_counter.items() if count >= min_support}
    if not frequent_items:
        return (None, {})
    root = FPTreeNode(None, 0, None)
    header_table = defaultdict(lambda : None)
    for transaction in transactions:
        sorted_items = sorted([item for item in transaction if item in frequent_items], key=lambda item: (-item_counter[item], item))
        current_node = root
        for item in sorted_items:
            if item in current_node.children:
                current_node.children[item].increment(1)
            else:
                new_node = FPTreeNode(item, 1, current_node)
                current_node.children[item] = new_node
                if header_table[item] is None:
                    header_table[item] = new_node
                else:
                    current_link = header_table[item]
                    while current_link.link is not None:
                        current_link = current_link.link
                    current_link.link = new_node
            current_node = current_node.children[item]
    return (root, header_table)
