import numpy as np
def knn_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int, metric: str='euclidean') -> np.ndarray:
    """Predict labels for X_test using k-NN with the specified metric."""

    def euclidean_distance(a, b):
        return np.sqrt(np.sum((a - b) ** 2, axis=1))

    def manhattan_distance(a, b):
        return np.sum(np.abs(a - b), axis=1)

    def cosine_distance(a, b):
        dot_product = np.sum(a * b, axis=1)
        norm_a = np.linalg.norm(a, axis=1)
        norm_b = np.linalg.norm(b, axis=1)
        return 1 - dot_product / (norm_a * norm_b)
    if metric == 'manhattan':
        distance_func = manhattan_distance
    elif metric == 'cosine':
        distance_func = cosine_distance
    else:
        distance_func = euclidean_distance
    predictions = []
    for test_sample in X_test:
        distances = distance_func(X_train, test_sample)
        nearest_indices = np.argsort(distances)[:k]
        nearest_labels = y_train[nearest_indices]
        (unique_labels, counts) = np.unique(nearest_labels, return_counts=True)
        majority_vote = unique_labels[np.argmax(counts)]
        predictions.append(majority_vote)
    return np.array(predictions)

