def metric(name: str):

    def validate_vectors(x, y):
        if not isinstance(x, (list, tuple, np.ndarray)) or not isinstance(y, (list, tuple, np.ndarray)):
            return False
        x = np.array(x, dtype=float)
        y = np.array(y, dtype=float)
        if x.ndim != 1 or y.ndim != 1 or len(x) == 0 or (len(y) == 0) or (len(x) != len(y)):
            return False
        return (x, y)

    def euclidean(x, y):
        (x, y) = validate_vectors(x, y)
        if x is False:
            return -1
        return round(np.sqrt(np.sum((x - y) ** 2)), 4)

    def manhattan(x, y):
        (x, y) = validate_vectors(x, y)
        if x is False:
            return -1
        return round(np.sum(np.abs(x - y)), 4)

    def chebyshev(x, y):
        (x, y) = validate_vectors(x, y)
        if x is False:
            return -1
        return round(np.max(np.abs(x - y)), 4)

    def cosine(x, y):
        (x, y) = validate_vectors(x, y)
        if x is False:
            return -1
        norm_x = np.linalg.norm(x)
        norm_y = np.linalg.norm(y)
        if norm_x == 0 or norm_y == 0:
            return -1
        cosine_similarity = np.dot(x, y) / (norm_x * norm_y)
        cosine_distance = 1 - cosine_similarity
        return round(cosine_distance, 4)
    metrics = {'euclidean': euclidean, 'manhattan': manhattan, 'chebyshev': chebyshev, 'cosine': cosine}
    return metrics.get(name, lambda x, y: -1)

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def hand_value(hand: list[int]) -> int:
        """Calculate the score of a hand, considering usable aces."""
        total = sum(hand)
        if 1 in hand and total + 10 <= 21:
            total += 10
        return total

    def is_natural_blackjack(hand: list[int]) -> bool:
        """Check if the hand is a natural blackjack."""
        return len(hand) == 2 and 1 in hand and (10 in hand)
    player_score = hand_value(player)
    dealer_score = hand_value(dealer)
    player_bust = player_score > 21
    dealer_bust = dealer_score > 21
    if player_bust:
        return -1.0
    if dealer_bust:
        return 1.0
    if player_score > dealer_score:
        if is_natural_blackjack(player):
            return 1.5
        return 1.0
    if player_score < dealer_score:
        return -1.0
    return 0.0

