def gbdt_regression(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_estimators: int=10, learning_rate: float=0.1, max_depth: int=3) -> list[float]:
    """Gradient Boosting Decision Tree (GBDT) regressor."""

    def mean_squared_error(y_true, y_pred):
        return np.mean((y_true - y_pred) ** 2)

    def fit_tree(X, y, max_depth):
        if max_depth == 0 or len(np.unique(y)) == 1:
            return np.mean(y)
        best_split = None
        best_mse = float('inf')
        best_left_indices = None
        best_right_indices = None
        for feature_index in range(X.shape[1]):
            for split_value in np.unique(X[:, feature_index]):
                left_indices = X[:, feature_index] <= split_value
                right_indices = X[:, feature_index] > split_value
                if np.sum(left_indices) == 0 or np.sum(right_indices) == 0:
                    continue
                left_mean = np.mean(y[left_indices])
                right_mean = np.mean(y[right_indices])
                mse = (mean_squared_error(y[left_indices], left_mean) * np.sum(left_indices) + mean_squared_error(y[right_indices], right_mean) * np.sum(right_indices)) / len(y)
                if mse < best_mse:
                    best_mse = mse
                    best_split = (feature_index, split_value)
                    best_left_indices = left_indices
                    best_right_indices = right_indices
        if best_split is None:
            return np.mean(y)
        left_tree = fit_tree(X[best_left_indices], y[best_left_indices], max_depth - 1)
        right_tree = fit_tree(X[best_right_indices], y[best_right_indices], max_depth - 1)
        return (best_split, left_tree, right_tree)

    def predict_tree(tree, X):
        if not isinstance(tree, tuple):
            return np.full(X.shape[0], tree)
        (feature_index, split_value) = tree[0]
        (left_tree, right_tree) = (tree[1], tree[2])
        left_indices = X[:, feature_index] <= split_value
        right_indices = X[:, feature_index] > split_value
        predictions = np.empty(X.shape[0])
        predictions[left_indices] = predict_tree(left_tree, X[left_indices])
        predictions[right_indices] = predict_tree(right_tree, X[right_indices])
        return predictions
    initial_prediction = np.mean(y_train)
    predictions = np.full(y_train.shape, initial_prediction)
    for _ in range(n_estimators):
        residuals = y_train - predictions
        tree = fit_tree(X_train, residuals, max_depth)
        predictions += learning_rate * predict_tree(tree, X_train)
    final_predictions = initial_prediction + learning_rate * predict_tree(tree, X_test)
    return list(np.round(final_predictions, 4))

