def regression_tree_predict(X_train: list[float | int], y_train: list[float | int], X_test: list[float | int], max_depth: int=3) -> list[float]:
    """Fit a univariate regression tree of depth *max_depth* and predict.

    Parameters
    ----------
    X_train : list[float | int]
        1-D feature values for the training set.
    y_train : list[float | int]
        Continuous target values for the training set.
    X_test : list[float | int]
        1-D feature values for which predictions are required.
    max_depth : int, default 3
        Maximum depth of the binary tree (root has depth 0).

    Returns
    -------
    list[float]
        Predictions for every element in *X_test*.
    """
    if not X_train:
        return []

    def calculate_sse(y):
        if len(y) == 0:
            return 0
        mean_y = np.mean(y)
        return np.sum((y - mean_y) ** 2)

    def find_best_split(X, y):
        best_sse = float('inf')
        best_split = None
        best_left = None
        best_right = None
        for i in range(1, len(X)):
            left_y = y[:i]
            right_y = y[i:]
            sse = calculate_sse(left_y) + calculate_sse(right_y)
            if sse < best_sse:
                best_sse = sse
                best_split = X[i]
                best_left = (X[:i], left_y)
                best_right = (X[i:], right_y)
        return (best_split, best_left, best_right)

    def build_tree(X, y, depth):
        if len(X) < 2 or depth == max_depth:
            return np.mean(y)
        (split, (left_X, left_y), (right_X, right_y)) = find_best_split(X, y)
        if split is None:
            return np.mean(y)
        left_tree = build_tree(left_X, left_y, depth + 1)
        right_tree = build_tree(right_X, right_y, depth + 1)
        return (split, left_tree, right_tree)
    tree = build_tree(X_train, y_train, 0)

    def predict(tree, x):
        if isinstance(tree, float):
            return tree
        (split, left_tree, right_tree) = tree
        if x < split:
            return predict(left_tree, x)
        else:
            return predict(right_tree, x)
    predictions = [predict(tree, x) for x in X_test]
    return predictions

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """

    def compute_gradient(x):
        return np.round(x * (1 - x), 4)
    if isinstance(z, (int, float)):
        return compute_gradient(z)
    elif isinstance(z, list):
        return [sigmoid_grad(sub_z) for sub_z in z]
    elif isinstance(z, np.ndarray):
        return np.round(z * (1 - z), 4).tolist()
    else:
        raise TypeError('Input must be a float, list, or NumPy ndarray.')

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    current_node = root
    while current_node.y is None:
        if task_type == 'classification':
            feature_value = features[current_node.label]
            matched_child = None
            for child in current_node.child:
                if child.x == feature_value:
                    matched_child = child
                    break
            current_node = matched_child if matched_child else current_node.child[1]
        elif task_type == 'regression':
            feature_value = features[current_node.label]
            if feature_value <= current_node.s:
                current_node = current_node.child[0]
            else:
                current_node = current_node.child[1]
    return current_node.y

