import numpy as np
def euclidean_distance_matrix(data):
    """Compute the full pair-wise Euclidean distance matrix."""
    n_samples = data.shape[0]
    distance_matrix = np.zeros((n_samples, n_samples))
    for i in range(n_samples):
        for j in range(n_samples):
            distance_matrix[i, j] = np.linalg.norm(data[i] - data[j])
    return distance_matrix
def k_distance_and_neighbors(distance_matrix, k):
    """Compute k-distance and k nearest neighbors for each sample."""
    n_samples = distance_matrix.shape[0]
    k_distances = np.zeros(n_samples)
    neighbors = []
    for i in range(n_samples):
        sorted_indices = np.argsort(distance_matrix[i])
        k_distances[i] = distance_matrix[i, sorted_indices[k]]
        neighbors.append(sorted_indices[:k])
    return (k_distances, neighbors)
def reachability_distance(distance_matrix, k_distances, p, q):
    """Compute the reachability distance between two points."""
    return max(k_distances[q], distance_matrix[p, q])
def local_reachability_density(distance_matrix, k_distances, neighbors, p, k):
    """Compute the Local Reachability Density (LRD) of a point."""
    reach_dists = [reachability_distance(distance_matrix, k_distances, p, q) for q in neighbors[p]]
    return k / np.sum(reach_dists)
def local_outlier_factor(data, k, epsilon=1.5):
    """Compute the Local Outlier Factor (LOF) for each sample."""
    distance_matrix = euclidean_distance_matrix(data)
    (k_distances, neighbors) = k_distance_and_neighbors(distance_matrix, k)
    n_samples = data.shape[0]
    lrd = np.zeros(n_samples)
    for i in range(n_samples):
        lrd[i] = local_reachability_density(distance_matrix, k_distances, neighbors, i, k)
    lof_scores = np.zeros(n_samples)
    for i in range(n_samples):
        lrd_ratios = [lrd[q] / lrd[i] for q in neighbors[i]]
        lof_scores[i] = np.sum(lrd_ratios) / k
    lof_scores = np.round(lof_scores, 4)
    outlier_indices = np.where(lof_scores > epsilon)[0]
    return (lof_scores.tolist(), sorted(outlier_indices.tolist()))
