def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    """Return all frequent itemsets in **transactions**.

    Args:
        transactions: List where each element represents a transaction and is
                       itself a list of items (hashable Python objects).
        minsup:       Minimum support given as a fraction between 0 and 1.

    Returns:
        A dictionary that maps every frequent itemset (stored as a tuple of
        sorted items) to its absolute support count.  If no itemset reaches
        the threshold the function must return an empty dictionary.
    """
    min_support_count = math.ceil(minsup * len(transactions))
    frequent_itemsets = {}
    for k in range(1, len(max(transactions, key=len)) + 1):
        itemsets = set()
        for transaction in transactions:
            itemsets.update(combinations(sorted(transaction), k))
        itemset_counts = Counter()
        for transaction in transactions:
            for itemset in itemsets:
                if set(itemset).issubset(set(transaction)):
                    itemset_counts[itemset] += 1
        for (itemset, count) in itemset_counts.items():
            if count >= min_support_count:
                frequent_itemsets[itemset] = count
    return frequent_itemsets

import numpy as np
def softplus(x, deriv: bool=False):
    """
    SoftPlus activation function with gradient.

    Parameters:
    x (scalar, list, or NumPy array): Input values.
    deriv (bool, optional): Whether to compute the gradient. Defaults to False.

    Returns:
    list or float: SoftPlus(x) or sigmoid(x) values, rounded to 4th decimal.
    """
    x = np.asarray(x)
    if deriv:
        sigmoid_x = np.where(x < 0, np.exp(x) / (1 + np.exp(x)), 1 / (1 + np.exp(-x)))
        result = sigmoid_x
    else:
        softplus_x = np.where(x < 0, np.log1p(np.exp(x)), x + np.log1p(np.exp(-x)))
        result = softplus_x
    result = np.round(result, 4)
    if result.size == 1:
        return result.item()
    else:
        return result.tolist()

