def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*.

    Your task is to complete this function so that it builds N-gram count
    tables from *corpus* and then returns the total log-probability of
    *sequence* under the resulting unsmoothed language model.

    The return value must be rounded to 4 decimal places.  If any required
    count is zero you should immediately return ``float('-inf')``.
    """
    ngram_counts = {}
    for i in range(len(corpus) - N + 1):
        ngram = tuple(corpus[i:i + N])
        if ngram not in ngram_counts:
            ngram_counts[ngram] = 0
        ngram_counts[ngram] += 1
    if N > 1:
        prefix_counts = {}
        for i in range(len(corpus) - N + 1):
            prefix = tuple(corpus[i:i + N - 1])
            if prefix not in prefix_counts:
                prefix_counts[prefix] = 0
            prefix_counts[prefix] += 1
    else:
        prefix_counts = None
    log_prob = 0.0
    for i in range(len(sequence) - N + 1):
        ngram = tuple(sequence[i:i + N])
        if ngram not in ngram_counts:
            return float('-inf')
        count = ngram_counts[ngram]
        if N == 1:
            prob = count / len(corpus)
        else:
            prefix = tuple(sequence[i:i + N - 1])
            if prefix not in prefix_counts:
                return float('-inf')
            prob = count / prefix_counts[prefix]
        log_prob += np.log(prob)
    return round(log_prob, 4)

import numpy as np
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test.

    Parameters
    ----------
    X_train : np.ndarray
        Training feature matrix of shape (n_samples, n_features).
    y_train : np.ndarray
        Integer class labels for the training data, shape (n_samples,).
    X_test : np.ndarray
        Feature matrix to classify, shape (m_samples, n_features).
    max_depth : int | None, optional
        Maximum allowed depth of the tree. If None the depth is unlimited.

    Returns
    -------
    list[int]
        Predicted class label for each row in X_test.
    """

    def gini_impurity(y: np.ndarray) -> float:
        """Calculate Gini impurity for a given set of labels."""
        p = np.array([np.mean(y == c) for c in np.unique(y)])
        return 1 - np.sum(p ** 2)

    def find_best_split(X: np.ndarray, y: np.ndarray) -> tuple[int, float]:
        """Find the best split for a given set of features and labels."""
        best_feature = -1
        best_threshold = -1
        best_gini_gain = -1
        for feature_idx in range(X.shape[1]):
            thresholds = np.unique(X[:, feature_idx])
            for i in range(len(thresholds) - 1):
                threshold = (thresholds[i] + thresholds[i + 1]) / 2
                left_y = y[X[:, feature_idx] < threshold]
                right_y = y[X[:, feature_idx] >= threshold]
                left_gini = gini_impurity(left_y)
                right_gini = gini_impurity(right_y)
                gini_gain = gini_impurity(y) - len(left_y) / len(y) * left_gini - len(right_y) / len(y) * right_gini
                if gini_gain > best_gini_gain:
                    best_gini_gain = gini_gain
                    best_feature = feature_idx
                    best_threshold = threshold
        return (best_feature, best_threshold)

    def build_tree(X: np.ndarray, y: np.ndarray, depth: int=0) -> dict:
        """Build a decision tree recursively."""
        if max_depth is not None and depth >= max_depth:
            return {'leaf': np.bincount(y).argmax()}
        if len(np.unique(y)) == 1:
            return {'leaf': y[0]}
        (feature_idx, threshold) = find_best_split(X, y)
        if feature_idx == -1:
            return {'leaf': np.bincount(y).argmax()}
        left_X = X[X[:, feature_idx] < threshold]
        left_y = y[X[:, feature_idx] < threshold]
        right_X = X[X[:, feature_idx] >= threshold]
        right_y = y[X[:, feature_idx] >= threshold]
        if len(left_y) == 0 or len(right_y) == 0:
            return {'leaf': np.bincount(y).argmax()}
        return {'feature': feature_idx, 'threshold': threshold, 'left': build_tree(left_X, left_y, depth + 1), 'right': build_tree(right_X, right_y, depth + 1)}
    tree = build_tree(X_train, y_train)

    def predict(X: np.ndarray, tree: dict) -> int:
        """Make a prediction for a given sample."""
        if 'leaf' in tree:
            return tree['leaf']
        feature_idx = tree['feature']
        threshold = tree['threshold']
        if X[feature_idx] < threshold:
            return predict(X, tree['left'])
        else:
            return predict(X, tree['right'])
    predictions = [predict(x, tree) for x in X_test]
    return predictions

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def calculate_score(hand: list[int]) -> int:
        """Calculate the score of a Blackjack hand.

        If the hand contains an Ace and the sum + 10 is ≤ 21, exactly one Ace can be counted as 11 instead of 1.

        Args:
            hand: List of integers (1–10) representing a Blackjack hand.

        Returns:
            The highest legal score of the hand not larger than 21.
        """
        total = sum(hand)
        if 1 in hand and total + 10 <= 21:
            total += 10
        return min(total, 21)
    player_score = calculate_score(player)
    dealer_score = calculate_score(dealer)
    if player_score > 21:
        return -1
    if dealer_score > 21:
        if len(player) == 2 and 1 in player and (10 in player):
            return 1.5
        else:
            return 1
    if player_score > dealer_score:
        if len(player) == 2 and 1 in player and (10 in player):
            return 1.5
        else:
            return 1
    elif player_score < dealer_score:
        return -1
    else:
        return 0

def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool) -> PyTuple[bool, bool]:
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def is_space_continuous(space: Space, is_composite: bool) -> bool:
        """Check if a space is continuous."""
        if not is_composite:
            return isinstance(space, Box)
        elif isinstance(space, (Tuple, Dict)):
            if isinstance(space, Tuple):
                sub_spaces = space.spaces
            else:
                sub_spaces = space.spaces.values()
            return all((isinstance(sub_space, Box) for sub_space in sub_spaces))
        else:
            raise ValueError('Composite space must be Tuple or Dict')
    cont_action = is_space_continuous(env.action_space, tuple_action)
    cont_obs = is_space_continuous(env.observation_space, tuple_obs)
    return (cont_action, cont_obs)

