from typing import Hashable
def apriori_frequent_itemsets(transactions: list[list[Hashable]], min_sup: float) -> list[tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """
    item_counts = {}
    for transaction in transactions:
        for item in transaction:
            item_counts[item] = item_counts.get(item, 0) + 1
    frequent_items = {item for (item, count) in item_counts.items() if count / len(transactions) >= min_sup}
    result = [(item,) for item in sorted(frequent_items)]
    k = 2
    while True:
        candidates = set()
        for itemset in result:
            if len(itemset) == k - 1:
                for item in frequent_items:
                    if item > itemset[-1]:
                        candidates.add(itemset + (item,))
        pruned_candidates = set()
        for candidate in candidates:
            for subset in itertools.combinations(candidate, k - 1):
                if subset not in result:
                    break
            else:
                pruned_candidates.add(candidate)
        candidate_counts = {}
        for transaction in transactions:
            for candidate in pruned_candidates:
                if set(candidate).issubset(transaction):
                    candidate_counts[candidate] = candidate_counts.get(candidate, 0) + 1
        frequent_candidates = [candidate for (candidate, count) in candidate_counts.items() if count / len(transactions) >= min_sup]
        if not frequent_candidates:
            break
        result.extend(frequent_candidates)
        k += 1
    return result

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    X = np.array(X)
    if Y is None:
        Y = X
    else:
        Y = np.array(Y)
    if X.shape[1] != Y.shape[1]:
        return -1
    C = X.shape[1]
    if sigma is None:
        sigma = np.sqrt(C / 2)
    if isinstance(sigma, (int, float)):
        if sigma <= 0:
            return -1
        sigma = np.full(C, sigma)
    elif isinstance(sigma, list):
        if len(sigma) != C:
            return -1
        sigma = np.array(sigma)
        if np.any(sigma <= 0):
            return -1
    dist_sq = np.sum((X[:, np.newaxis] - Y) ** 2 / sigma ** 2, axis=2)
    kernel_matrix = np.exp(-0.5 * dist_sq)
    kernel_matrix = np.round(kernel_matrix, 4).tolist()
    return kernel_matrix

