import itertools
def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """
    item_counts = Counter((item for transaction in transactions for item in transaction))
    frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_sup}
    sorted_transactions = [[item for item in transaction if item in frequent_items] for transaction in transactions]
    sorted_transactions = [sorted(transaction, key=lambda item: frequent_items[item], reverse=True) for transaction in sorted_transactions]
    fp_tree = FPtree()
    for transaction in sorted_transactions:
        fp_tree.insert(transaction)
    frequent_itemsets = []
    for item in fp_tree.header_table:
        conditional_pattern_base = fp_tree.get_conditional_pattern_base(item)
        conditional_fp_tree = FPtree()
        for transaction in conditional_pattern_base:
            conditional_fp_tree.insert(transaction)
        if conditional_fp_tree.is_single_path():
            path_items = conditional_fp_tree.get_path_items()
            for r in range(1, len(path_items) + 1):
                for combination in itertools.combinations(path_items, r):
                    frequent_itemsets.append(tuple(sorted(combination)))
        else:
            recursive_itemsets = fp_growth(conditional_pattern_base, min_sup)
            frequent_itemsets.extend(recursive_itemsets)
    frequent_itemsets.extend([(item,) for item in frequent_items])
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets
class FPtree:

    def __init__(self):
        self.root = Node()
        self.header_table = {}

    def insert(self, transaction):
        current_node = self.root
        for item in transaction:
            if item not in current_node.children:
                new_node = Node()
                current_node.children[item] = new_node
                self.header_table.setdefault(item, []).append(new_node)
            current_node = current_node.children[item]
        current_node.count += 1

    def get_conditional_pattern_base(self, item):
        pattern_base = []
        for node in self.header_table[item]:
            transaction = []
            current_node = node.parent
            while current_node is not self.root:
                transaction.append(current_node.item)
                current_node = current_node.parent
            transaction.reverse()
            pattern_base.append(transaction)
        return pattern_base

    def is_single_path(self):
        return len(self.root.children) == 1 and all((len(child.children) <= 1 for child in self.root.children.values()))

    def get_path_items(self):
        path_items = []
        current_node = self.root
        while current_node.children:
            item = next(iter(current_node.children))
            path_items.append(item)
            current_node = current_node.children[item]
        return path_items
class Node:

    def __init__(self):
        self.children = {}
        self.count = 0
        self.parent = None
        self.item = None

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """
    if isinstance(z, (int, float)):
        return round(z * (1 - z), 4)
    elif isinstance(z, list):
        if any((isinstance(i, list) for i in z)):
            return [sigmoid_grad(i) for i in z]
        else:
            return [round(i * (1 - i), 4) for i in z]
    elif isinstance(z, np.ndarray):
        return np.round(z * (1 - z), 4).tolist()
    elif z == []:
        return []
    else:
        raise ValueError('Invalid input type. Expected scalar, list or NumPy array.')

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if hasattr(root, 'y'):
        return root.y
    if task_type == 'classification':
        feature_value = features[root.label]
        for child in root.child:
            if child.x == feature_value:
                return predict_sample(child, features, task_type)
        return predict_sample(root.child[1], features, task_type)
    elif task_type == 'regression':
        feature_value = features[root.label]
        if feature_value <= root.s:
            return predict_sample(root.child[0], features, task_type)
        else:
            return predict_sample(root.child[1], features, task_type)
    raise ValueError("Invalid task_type. Must be 'classification' or 'regression'.")

