import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)
    (n_samples, n_features) = data.shape
    height_limit = math.ceil(math.log2(sample_size))

    def c(n):
        if n == 1:
            return 0
        else:
            return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    def build_tree(X):
        if X.shape[0] <= 1 or (X.shape[0] == sample_size and height_limit == 0):
            return LeafNode(X)
        else:
            feature_idx = np.random.randint(0, n_features)
            threshold = np.random.uniform(X[:, feature_idx].min(), X[:, feature_idx].max())
            left_child = X[X[:, feature_idx] < threshold]
            right_child = X[X[:, feature_idx] >= threshold]
            return InternalNode(feature_idx, threshold, build_tree(left_child), build_tree(right_child))

    def path_length(x, tree):
        if isinstance(tree, LeafNode):
            return c(tree.samples.shape[0])
        elif x[tree.feature_idx] < tree.threshold:
            return 1 + path_length(x, tree.left_child)
        else:
            return 1 + path_length(x, tree.right_child)

    class LeafNode:

        def __init__(self, samples):
            self.samples = samples

    class InternalNode:

        def __init__(self, feature_idx, threshold, left_child, right_child):
            self.feature_idx = feature_idx
            self.threshold = threshold
            self.left_child = left_child
            self.right_child = right_child
    trees = []
    for _ in range(n_trees):
        subsample_idx = np.random.choice(n_samples, sample_size, replace=False)
        subsample = data[subsample_idx]
        tree = build_tree(subsample)
        trees.append(tree)
    path_lengths = np.zeros(n_samples)
    for i in range(n_samples):
        x = data[i]
        path_lengths[i] = np.mean([path_length(x, tree) for tree in trees])
    phi = 2 * math.log(n_samples - 1) - 2 * (n_samples - 1) / n_samples
    scores = 2 ** (-path_lengths / phi)
    if epsilon == 0:
        return []
    else:
        threshold = np.percentile(scores, 100 * (1 - epsilon))
        outlier_idx = np.where(scores > threshold)[0]
        return sorted(outlier_idx.tolist())
