import numpy as np
def softplus(x, deriv: bool=False):
    """
    SoftPlus activation function and its derivative.

    Parameters:
    x (scalar, list, or NumPy array): Input values.
    deriv (bool, optional): Whether to compute the derivative (default: False).

    Returns:
    scalar or list: SoftPlus(x) or its derivative (element-wise sigmoid of x).
    """
    x = np.array(x)
    if not deriv:
        softplus_x = np.log1p(np.exp(x))
    else:
        sigmoid_x = 1 / (1 + np.exp(-x))
        softplus_x = sigmoid_x
    softplus_x = np.round(softplus_x, 4)
    if x.size == 1:
        return softplus_x.item()
    else:
        return softplus_x.tolist()

