def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*.

    Your task is to complete this function so that it builds N-gram count
    tables from *corpus* and then returns the total log-probability of
    *sequence* under the resulting unsmoothed language model.

    The return value must be rounded to 4 decimal places.  If any required
    count is zero you should immediately return ``float('-inf')``.
    """
    ngram_counts = {}
    for i in range(len(corpus) - N + 1):
        ngram = tuple(corpus[i:i + N])
        if ngram not in ngram_counts:
            ngram_counts[ngram] = 0
        ngram_counts[ngram] += 1
    prefix_counts = {}
    for i in range(len(corpus) - N + 1):
        prefix = tuple(corpus[i:i + N - 1])
        if prefix not in prefix_counts:
            prefix_counts[prefix] = 0
        prefix_counts[prefix] += 1
    log_prob = 0.0
    for i in range(len(sequence) - N + 1):
        ngram = tuple(sequence[i:i + N])
        prefix = tuple(sequence[i:i + N - 1])
        if ngram not in ngram_counts or prefix not in prefix_counts:
            return float('-inf')
        prob = ngram_counts[ngram] / prefix_counts[prefix]
        log_prob += np.log(prob)
    return round(log_prob, 4)

import numpy as np
def compute_impurity(y, criterion):
    """
    Compute a node's impurity.

    Args:
        y (numpy.ndarray): 1-D array with the target values.
        criterion (str): One of {"entropy", "gini", "mse"}.

    Returns:
        float: Impurity value rounded to 4 decimal places.

    Raises:
        ValueError: If criterion is not one of {"entropy", "gini", "mse"}.
    """
    if criterion not in ['entropy', 'gini', 'mse']:
        raise ValueError("Invalid criterion. Must be one of {'entropy', 'gini', 'mse'}")
    if len(y) == 0:
        return 0.0
    p = np.bincount(y) / len(y)
    if criterion == 'entropy':
        return -np.sum(p * np.log2(p))
    elif criterion == 'gini':
        return 1 - np.sum(p ** 2)
    elif criterion == 'mse':
        return np.mean((p - 0.5) ** 2)

import numpy as np
def forward_algorithm(S: list[float], A: list[list[float]], B: list[list[float]], observations: list[int]) -> float:
    """Forward algorithm for Hidden Markov Models.

    Args:
        S (list[float]): Initial state probabilities.
        A (list[list[float]]): State–transition probabilities.
        B (list[list[float]]): Emission probabilities.
        observations (list[int]): Observation index sequence.

    Returns:
        float: Sequence likelihood rounded to 4 decimals, or −1 on invalid input.
    """
    n = len(S)
    m = len(B[0])
    if len(A) != n or any((len(row) != n for row in A)) or any((len(row) != m for row in B)):
        return -1
    if not observations:
        return -1
    for obs in observations:
        if obs < 0 or obs >= m:
            return -1
    T = len(observations)
    forward_vars = np.zeros((n, T + 1))
    forward_vars[:, 0] = S
    for t in range(1, T + 1):
        for i in range(n):
            for j in range(n):
                forward_vars[i, t] += forward_vars[j, t - 1] * A[j][i]
            forward_vars[i, t] *= B[i][observations[t - 1]]
    likelihood = np.sum(forward_vars[:, T])
    return round(likelihood, 4)

