        raise ValueError('Unsupported input type')

def accuracy_score(y_true, y_pred):
    """Return the classification accuracy between *y_true* and *y_pred*.

    The function must:
      • return ‑1 if the two containers have different lengths;
      • return 0 when both containers are empty;
      • otherwise return the proportion of matching elements rounded to
        4 decimal places.

    Args:
        y_true (list | tuple): Ground-truth class labels.
        y_pred (list | tuple): Predicted class labels.

    Returns:
        float | int: The accuracy or ‑1 according to the rules above.
    """
    if len(y_true) != len(y_pred):
        return -1
    if len(y_true) == 0:
        return 0
    matches = sum((1 for (t, p) in zip(y_true, y_pred) if t == p))
    return round(matches / len(y_true), 4)

import numpy as np
def adaboost_predict(X_train: list[list[int | float]], y_train: list[int], X_test: list[list[int | float]], n_estimators: int=10) -> list[int]:
    X_train = np.array(X_train)
    y_train = np.array(y_train)
    X_test = np.array(X_test)
    w = np.ones(len(y_train)) / len(y_train)
    stumps = []
    for _ in range(n_estimators):
        best_stump = find_best_stump(X_train, y_train, w)
        stumps.append(best_stump)
        w = update_weights(w, best_stump, X_train, y_train)
        w = w / w.sum()
        if np.all(w * (y_train != best_stump['h'])) == 0:
            break
    predictions = []
    for x in X_test:
        vote = 0
        for stump in stumps:
            vote += stump['α'] * stump['h'](x)
        predictions.append(np.sign(vote).astype(int))
    return predictions
def find_best_stump(X_train: np.ndarray, y_train: np.ndarray, w: np.ndarray) -> dict:
    best_error = 1
    best_stump = None
    for feature_index in range(X_train.shape[1]):
        for threshold in np.unique(X_train[:, feature_index]):
            for polarity in [-1, 1]:
                error = weighted_error(X_train, y_train, w, feature_index, threshold, polarity)
                if error < best_error:
                    best_error = error
                    best_stump = {'feature_index': feature_index, 'threshold': threshold, 'polarity': polarity, 'α': 0, 'h': lambda x: polarity * (x[feature_index] < threshold)}
    return best_stump
def weighted_error(X_train: np.ndarray, y_train: np.ndarray, w: np.ndarray, feature_index: int, threshold: float, polarity: int) -> float:
    error = np.sum(w * (y_train != polarity * (X_train[:, feature_index] < threshold)))
    return error
def update_weights(w: np.ndarray, stump: dict, X_train: np.ndarray, y_train: np.ndarray) -> np.ndarray:
    error = weighted_error(X_train, y_train, w, stump['feature_index'], stump['threshold'], stump['polarity'])
    α = 0.5 * np.log((1 - error) / (error + 1e-10))
    w = w * np.exp(-α * y_train * stump['polarity'] * (X_train[:, stump['feature_index']] < stump['threshold']))
    return w

